INSERT INTO Books (ISBN, Image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123322, './Images/159.jpg', '101 Gjëra Që Duhet Të Dini Për Kafshët', '2024-05-30T16:24:00.024Z', 51, 'Preke botën e mahnitshme e të mistershme të kafshëve, duke njohur me imtësi e kërshëri faunën e çdo habitati, që nga savana afrikane deri në pole. Mëso për krijesat që banojnë anekënd planetit dhe për mënyrën e tyre të jetesës. Zbulo më të egrat, më të çuditshmet, madje edhe ato që shkojnë pa u vënë re. Këtu ka gjithçka që duhet të njohë çdo i apasionuar pas mbretërisë së kafshëve, shoqëruar me ilustrime e kuriozitete interesante..', 3.00, '2024-05-30T16:24:00.024Z', 'Paper', 28, 35);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123321, './Images/1.jpg', '201 Kryefabula', '2024-05-30T16:24:00.024Z', 255, 'Njeriu mundë të shkruaj tërë jetën dhe të mos arrij të botoj as njëqind pjesë nga kjo sferë e mençurisë njerëzore. Duke lexuar fabulat e tij ndjen me një kënaqësi estetike vë buzën në gaz nga rrëkezat e humorit , ironisë dhe sarkazmës.', 4.00, '2024-05-30T16:24:00.024Z', 'Paper', 27, 89);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123323, './Images/172.jpg', 'Shpikësit E Mëdhenj', '2024-05-30T16:24:00.024Z', 123, '	Shpikësit më të mëdhenj dhe idetë e historitë e tyre, nga më të pazakonshmet, te më të dobishmet, nga më të çuditshmet, tek ato që ndryshuan përgjithmonë botën dhe zakonet njerëzore do t’ju shpalosen te ky libër, autorët e të cilit do t’ju rrëfejnë për gjenialitetin e mendjes njerëzore. Për çdo shkronjë të alfabetit do të njiheni me një shpikës dhe me shpikjen e tij më të spikatur. Kush e shpiku kinemanë? Kush e realizoi dhe si thirrjen e parë me një telefon celular? Kush e shpiku lojën me lego? Kush e shpiku telekomandën? Kush e krijoi kukullën Barbi dhe pse? Kush e shpiku radioteleskopin e parë? Po kompjuterin? Po play-stationin? Përgjigjet do t’i gjeni vetëm po të shfletoni këtë libër. Ilustrimet janë mahnitëse. ', 9.80, '2024-05-30T16:24:00.024Z', 'Paper', 28, 35);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123329, './Images/3.jpg', '1111 Barcoleta ', '2024-05-30T16:24:00.024Z', 123, ' Vendimi më i rëndësishëm që një njeri merr çdo ditë është ai i të qënit në humor të mirë. ', 6.00, '2024-05-30T16:24:00.024Z', 'Paper', 32, 4);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123367, './Images/44.jpg', 'Pese Fytyrat E Modernitetit', '2024-05-30T16:24:00.024Z', 398, 'Ky libër ambicioz e sqarues ofron një analizë të ideve estetike dhe intelektuale që drejtuan krijimtarinë letrare gjatë 150 viteve të fundit. Calinescu, diskuton me mendjemprehtësi të rrallë dhe elegancë rreth marrëdhënieve komplekse midis konceptesh që përdoren rëndom, por rrallëherë janë përcaktuar saktësisht. Duke vepruar kështu ai u jep një ndihmë të veçantë studimeve bashkëkohore dhe kritikës. Gjithmonë ka qenë e vështirë të datohet me përpikëri shfaqja e një koncepti, aq më tepër kur koncepti në shqyrtim ka qenë gjatë gjithë historisë së tij kaq i ndërlikuar dhe kaq i aftë për të mbjellë mospajtime sa koncepti i "modernitetit"." ', 12.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 21);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123380, './Images/57.jpg', 'Arti Dhe Mjeshteria E Bukurise ', '2024-05-30T16:24:00.024Z', 224, ' Qëllimi i këtij libri është të hedhë dritë mbi estetikën nga disa pikëvështrime, para së gjithash, për të treguar disa ide dhe tipare themelore. Ai përfshin haptas pikëpamje vlerëgjykuese dhe polemizuese, të cilat synojnë, si të thuash, të sfidojnë vetë pedagogët e artit që të mos i pranojnë, duke formuar kështu opinionet e tyre. Për një pedagog arti, estetika lidhet kryesisht me shqyrtimin e aftësive në shkallën më të gjerë të mundshme. Kjo sepse një aftësi nuk mund të ketë vetëm kuptimin se di të bësh diçka, një përsëritje mekanike pa kuptuar gjë nga konteksti. Aftësia gjithmonë thelbësore nënkupton rrokjen e parimeve operacionale, të etikës dhe përgjegjësisë. Ajo përfshin gjithashtu të qenit i aftë t’ua komunikosh këtë aftësi të tjerëve, të udhëheqësh dhe të mësosh.', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 7);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123415, './Images/94.jpg', 'Lufta Ime  ', '2024-05-30T16:24:00.024Z', 304, ' Mos e merrni këtë libër si propagandë të nazizmit. Kjo është historia. Historia jonë dhe juaja, historia botërore. Ky libër është i ndaluar me ligj që të përdoret për te u propaganduar dhe për te u ndjekur si program për aktivitet politik dhe në këndvështrimin etiko – moral. Prandaj, mendojmë se duhet lexuar me një sy kritik për të nxjerrë mësimet e duhura që na i ka dhënë vetë gjyqi i historisë.', 17.85, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123421, './Images/109.jpg', '50 Ide Ekonomike ', '2024-05-30T16:24:00.024Z', 209, 'Përshkrimi i Thomas Carlyle për të dhënat ekonomike që nga 1849, thjesht ka ngecur, për më mirë apo për më keq. Dikush zor se do të habitej. Ekonomia është diçka që njerzit zakonisht e venë re vetëm kur gjërat shkojnë keq. Vetëm kur një ekonomi ndodhet përballë një krize, kur mijra njerëz humbin punën, kur çmimet rriten së tepërmi ose kur bien shumë shpejt, atëherë ne i kushtojmë më shumë vëmendje subjektit të ekonomisë. Në të tilla momente, ekziston vetëm një dyshim i vogël që ky subjekt të duket i dështuar, në veçanti kur nxjerr në pah sfidat dhe pengesat me të cilat përballemi, bën të qartë realitetin se ne nuk mund të kemi çdo gjë që duam dhe ilustron faktin se krijesat njerëzore janë të papërsosura që në lindje.E vërteta, do të shtoja, në mënyrën tipike ekonomike, është shumë më pak e thjeshtë. Nëse do të ishte thjesht një studim shifrash, statistikash dhe teorish, atëherë analogjia e shkencës dëshpëruese ndoshta do të qëndronte më fort. ', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 13);
