INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123477, './Images/156.jpg', ' Libri I Madh I Shpikjeve', '2024-05-30T16:24:00.024Z', 80, 'Shpikjet e mëdha të njerëzimit e gjejnë veten më lehtë se kudo tjetër te mendja e të vegjëlve të cilët për nga natyra janë kuriozë për të mësuar sesi funksionojnë gjërat. Ky libër është një hyrje në botën e madhe të shkencës, - dhe vjen i kuruar plot kujdes nga botuesusi për fëmijët e vegjël ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 21, 10);
