INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123482, './Images/181.jpg', 'Mengjes Kampionesh ', '2024-05-30T16:24:00.024Z', 344, 'Shkrimtari i dështuar Kilgor Trofta dhe biznesmeni i suksesshëm Duejn Huver takohen në një festival artistik, në një qytezë të humbur të Miduestit amerikan. Prej këtij takimi, Huveri merr shtytjen e fundit drejt çmendurisë, ndërsa Trofta kthehet në figurë qendrore të letrave në Amerikë. Për Vonnegut, "Mëngjes kampionësh" është përsosja përfundimtare e mjeshtrisë së shkrimit, si dhe kritika e tij më e hidhur ndaj dështimit të "ëndrrës amerikane". ', 6.90, '2024-05-30T16:24:00.024Z', 'Paper', 31, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355489, './Images/392.jpg', 'Dje Sot Neser ', '2024-05-30T16:24:00.024Z', 332, ' Ajo tregon jetën e saj që nga fëmijëria si një vajzë e dobët e zeshkane, e më pas, në rininë e bukur por të pasigurt, e deri në rrugëtimin që do ta çonte këtë rioshe napoletane drejt lavdisë ndërkombëtare.Historia e Loren – it i ngjan më shumë një përralle, bashkë me ballot, sprovat e rëndësishme dhe princat e kaltër. E megjithatë, pas divës së kinemasë, buzëqesh një grua e ndrojtur dhe e vendosur, që ka vuajtur shumë, ka punuar edhe më shumë, ka dashuruar me pasion të vërtetë e me doza të lehta ironie.', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 7, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355650, './Images/380.jpg', 'Kohe Ndryshimesh ', '2024-05-30T16:24:00.024Z', 332, 'Eduard Dervishi, në librin e tij të pasur me fakte, dëshmi e analiza, na jep qartazi punën e tij dhe kolegëve në harkun kohor të pothuaj 7 viteve, duke na paraqitur ngjarje, njerëz dhe dokumente origjinale autentike, veprimtarinë e individëve dhe institucioneve. Ai analizon në këndvështrimin e zgjedhur prej tij, shoqërinë shqiptare para vitit 1990 dhe të viteve 1990-1997. ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 66, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355351, './Images/381.jpg', 'Kupa E Botes Dhe Shqiperia ', '2024-05-30T16:24:00.024Z', 189, 'Ndoshta për ta kuptuar më mirë synimin e këtij libri, ja se çfarë lexohet në nënkopertinën e tij si përmbajtje e asaj që pasqyrohet në të. Ajo çka vlen edhe më posaçëm është se kësaj radhe në këtë libër është ecuria e rezultateve të 291 ndeshjeve të Kombëtares që nga themelimi i saj e deri te ndeshja e fundit. ', 9.80, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
