INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123326, './Images/258.jpg', 'Kujtimet E Sherlock Homlsit', '2024-05-30T16:24:00.024Z', 266, ' Kujtimet e Sherlok Holmsit” është një përmbledhje e tregimeve të Sherlok Holmsit, e botuar fillimisht në vitin 1893, nga Artur Konan Dojli.Një burrë si Sherloku ka shumë armiq: vrasës të dhunshëm, batakçinj të ulët, fantazma dashurish të vjetra, kërcënues, shkrimtarë helmues, për të përmendur vetëm disa. Por askush nuk është kaq i tmerrshëm e i fuqishëm sa profesor Moriarti. Moriarti është i vetmi, i cili mund të krahasohet me gjenialitetin e Holmsit. Është i vetmi njeri, ndoshta, që mund ta mposhtë përfundimisht detektivin e madh...', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 28, 11);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123515, './Images/210.jpg', 'Bioletra ', '2024-05-30T16:24:00.024Z', 287, 'Kjo teori, Bioletra, shkrimin e leximin letrar i trajton si procese te nderliqshme e te lidhura njerëzore që kanë burim individin dhe botën e tij, e jo rrethat sociale e ideologjike te kolektivit. Kjo është kërkesë që letërsia t’i kthehet natyrës së vet të vërtetë, krijimit të vlerave shpirtërore dhe shijimit e njohjës së këtyre vlerave. ', 4.00, '2024-05-30T16:24:00.024Z', 'Paper', 66, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123483, './Images/177.png', 'Historia Pa Mbarim ', '2024-05-30T16:24:00.024Z', 223, 'Libri rrëfen historinë e Bastian Balthasar Bux, një djaloshi që gjen një libër magjik të titulluar "Historia Pa Mbarim". Ndërsa lexon, ai zbulon se mund të hyjë brenda botës së librit, Fantastica, dhe të ndërveprojë me personazhet e saj. ', 9.00, '2024-05-30T16:24:00.024Z', 'Paper', 65, 16);
