INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (1235538, './Images/315.jpg', 'Arti I Te Dashuruarit ', '2024-05-30T16:24:00.024Z', 145, '  Libri dëshiron të bindë lexuesin se të gjitha përpjekjet e tij për dashuri janë të caktuara të dështojnë nëse ai nuk përpiqet të zhvillojë më aktivisht në mënyrë sa më të plotë personalitetin e tij, me qëllim që të arrijë një orientim produktiv', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 32, 8);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123406, './Images/84.jpg', 'Ahmet Zogu ', '2024-05-30T16:24:00.024Z', 388, 'Duam a nuk duam ne, ai mbetet nje karakter interesant dhe enigmatik, nje perzierje pa kriter kontradiktash te ndryshme, gjysme hero dhe gjysme bufon, per te cilin do te interesohen si grumbulluesi i fakteve historike te çuditshme, ashtu dhe studiuesi i psikologjise.  ', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 51, 94);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123359, './Images/36.jpg', 'Mitet E Historiografisë Arbëreshe', '2024-05-30T16:24:00.024Z', 308, ' Ky libër përmbledh e shtjellon rezultatet fillestare të një projekti kërkimor më të gjerë e organik, të miratuar e të mbështetur nga Universiteti i Palermos. Thelbi i temave të rrahura, sikur-se edhe perspektiva e kërkimit në të cilën inkuadrohen ato, ka të bëjë me një studim të strukturuar, të ndërmarrë me synimin për të rindërtuar zanafillën dhe zhvillimin e identitetit kulturor arbëresh. Do saktësuar se ajo çka paraqitet këtu është vetëm pjesa destruens e projektit. Në të vërtetë, jo vetëm që janë mënjanuar përmes një shqyrtimi kritik falsifikimet në modë të historiografisë arbëreshe, por janë bërë përpjekje për ta vendosur hipotezën e kërkimit brenda atyre caqeve epistemologjike në të cilat, nga njëra anë akribia shkencore i bashkohet gjykimit gjakftohtë, ndërsa, nga ana tjetër, “falsi” (mitik ose historik) individualizohet jo vetëm për t’u dalluar nga “e vërteta”, por edhe për t’u peshuar me drejtësi që pastaj të flaket tej përfundimisht.', 13.30, '2024-05-30T16:24:00.024Z', 'Paper', 8, 7);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123419, './Images/98.jpg', 'Tetedhjete Vjet Tregime ', '2024-05-30T16:24:00.024Z', 321, 'Janë tetëdhjetë rrëfime që përmbledhin një jetë, shumë përvoja, ngjarje, e pjesëza historie të shkruara me shumë nostalgji, humor e gjuhë të pakomplikuar. Janë thesar si për autoren dhe fëmijët e saj, por edhe për lexuesin e një jete të përshkruar në këto tregime që e kanë pasur një arsye edhe peshë të madhe për t’u strehuar në kujtesën e saj. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 38, 17);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123531, './Images/226.jpg', 'Historia E Gjuhesise ', '2024-05-30T16:24:00.024Z', 376, ' Ky vëllim synon të i japë lexuesit një pamje të përgjithshme të rrethanave historike dhe të klimës kulturore, në të cilat panë dritën teoritë e ndryshme gjuhësore. Një histori e gjuhësisë, natyrisht, por edhe një histori e kulturës, që nga zanafilla në Greqinë e Lashtë, duke vijuar përmes zhvillimeve të botës romake, Mesjetës dhe Rilindjes e deri në kohët moderne.', 11.90, '2024-05-30T16:24:00.024Z', 'Paper', 58, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123551, './Images/253.jpg', 'Klubi Pikuik 2 ', '2024-05-30T16:24:00.024Z', 468, ' Pak nga pak, z. Uinkëll arriti te ia fitonte zemrën z. Benxhamin Alen, e madje edhe zhvilloi një bisedë miqësore me z. Bob Sojer, të cilin, i gjallëruar nga konjaku, silla dhe muhabeti, dalëngadalë e kaploi qesëndisja dhe tregoi me ngazëllim të njëmendtë një gazmore të bukur.', 16.00, '2024-05-30T16:24:00.024Z', 'Paper', 9, 11);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123504, './Images/195.jpg', 'Schopenhauer ', '2024-05-30T16:24:00.024Z', 152, ' Shopenhaueri është më i lexueshmi ndër tërë filozofët gjermanë. Ky libër na jep një shpjegim të ngjeshur dhe sa më përmbledhtas të sistemit të tij metafizik, duke u përqendruar në pamjet origjinale të mendimit të tij, që u shfaq si burim frymëzimi për artistë dhe mendimtarë të shumtë ', 4.80, '2024-05-30T16:24:00.024Z', 'Paper', 45, 19);
