INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355658, './Images/354.jpg', 'Fjalet E Ajshtainit', '2024-05-30T16:24:00.024Z', 198, ' Fjalët e këtyre protagonistëve të shkencës shërbejnë për t’iu futur temave të mëdha - metafora, ligjërimi, bukuria - të analizuara në pjesën e dytë. Ligjërimi i shkencës nuk mund të përjashtohet nga një nivel ambiguiteti, i quajtur “poetik”, që siguron qëndrueshmërinë. Një komunikim i efektshëm rifiton referimin me përvojën e përditshme dhe me përfytyrimin kolektiv; niveli poetik, në këtë kuptim, është thjeshtëzuar nga metaforat me të cilat shkenca dhe komunikimi i saj, është i pasur. ', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355449, './Images/355.jpg', '21 Leksione Per Shekullin E 21 ', '2024-05-30T16:24:00.024Z', 384, 'Autori i dy librave më të shitur të momentit në mbarë botën, "Sapiens" dhe “Homo Deus”, Youval Noah Harari vjen me librin më të fundit në shqip, “21 leksione për shekullin e 21”. Këtu analizohen sfidat globale si epidemitë, inteligjenca artificiale, edukimi, fetë, kombet, punësimi, mjedisi, demokracia, etj., dhe ofrohen zgjidhje të cilat i shërbjenë çdo individi në epokën tone. ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 21, 17);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355434, './Images/364.jpg', 'Kulture Kombetare Ne Kulturen Organizative ', '2024-05-30T16:24:00.024Z', 401, ' Dilema kryesore e autores është "A mund të bashkëjetojnë në paqe dhe në më shumë prosperitet individët e këtij planeti që janë të gjinive ,moshave,etnive,racave,dhe feve të ndryshme.Libri bazohet në një analizë teorike dhe mundohet të indentifikojë rreziqet dhe përfitimet që kanë njësit ekonomike të cilat përballen me diversitet kulturor . ', 9.00, '2024-05-30T16:24:00.024Z', 'Paper', 51, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355535, './Images/365.jpg', 'Studime Sociologjike ', '2024-05-30T16:24:00.024Z', 280, ' onceptet, analizat dhe teoritë e tij kanë arritur të kenë një domethënie universale në historinë e kësaj shkence, aq sa ai sot njihet si njëri nga sociologët më të mëdhenj të të gjithë kohërave. Veprat, studimet, esetë apo leksionet e tij (prej të cilave në këtë libër janë përzgjedhur më kryesoret) kanë arritur të ndikojnë më shumë se çdo mendimtar tjetër në analizën dhe kuptimin e botës moderne, të mënyrës së supremacisë së funksionimit të saj. ', 6.50, '2024-05-30T16:24:00.024Z', 'Paper', 45, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123369, './Images/46.jpg', '50 Ide Rreth Arkitektures', '2024-05-30T16:24:00.024Z', 210, 'Ky libër flet rreth ideve kryesore, të cilat krijuan bazën e arkitekturës Perëndimore që nga koha e Greqisë së lashtë deri në ditët tona. Këto ide përfshijnë një larmishmëri fushash që nga teknologjia deri te zbukurimi, që nga planifi kimi deri te zejtaria, po ashtu që nga mëhyra si të interpretojmë të kaluarën deri se si të ndërtojmë për të ardhmen. Këto ide përfshijnë frymëzimet intelektuale që krijuan stilin Gotik mesjetar, nocionet që gjenden pas idesë së qytetit-kopësht*) si dhe inovacionet teknologjike që krijuan grataçielat. Gjysma e parë e librit trajton të kaluarën e pasur të arkitekturës, që nga zanafi lla e saj - d.m.th. stili Grek - deri në zhvillimet revolucionare të fundit të shekullit XX. Kjo pjesë tregon se si arkitektët dhe ndërtuesit krijuan jo vetëm një fond të stileve – që nga stili Klasik deri te stili Gotik – por edhe të gjitha llojet e ideve – të tilla si parafabrikimi dhe qyteti-kopësht – të cilat janë me interes për arkitektët e ditëve të sotme. Gjysma e dytë e librit fi llon me rilindjen e madhe të shekullit XX. ', 8.90, '2024-05-30T16:24:00.024Z', 'Paper', 45, 17);
