INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123549, './Images/251.jpg', ' Vajza E Kapitenit', '2024-05-30T16:24:00.024Z', 388, 'Fragment nga libri: Im atë, Andrej Grinjovi, kur kishte qenë i ri kishte shërbyer pranë kontit Minih dhe kishe dalë në pension me gradën e majorit të parë në vitin 17... Që prej asaj kohe ai kishte jetuar në fshatin e tij, në Simbirsk dhe atje ishte martuar me Avdotja Vasiljevna Ju., e cila ishte bija e një fisniku të varfër të atij vendi. Ishim nëntë fëmijë. Motrat dhe vëllezërit më vdiqën që kur ishin të vegjël. Isha akoma në barkun e nënës, kur më regjistruan si reshter në regjimentin e Semjonovskit me ndërhyrjen e majorit të gardës princit B., që ishte kushëriri ynë i parë. ', 7.00 ,'2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123550, './Images/252.jpg', 'Mateo Falkone ', '2024-05-30T16:24:00.024Z', 242, 'Mateo Falkone është një histori e shkurtër por tejet komplekse ku në qender të veprës është vrasja e një djali 10-vjeçar nga babai i tij. ', 4.00, '2024-05-30T16:24:00.024Z', 'Paper', 53, 11);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123351, './Images/29.jpg', 'Grimca Nga Lashtësia', '2024-05-30T16:24:00.024Z', 125, 'Kur shpërtheu lufta midis Romakëve dhe Albanëve, Tullus Hostili, mbreti i Romakëve si dhe Metius Fufeti, mbreti i Albanëve, vendosën që ti jepnin fund luftës duke zhvilluar luftime një me një. Ndër Romakët ishin vëllezërit trinjakë, të quajtur Horacet, kishte edhe ndër Albanët trinjakë të quajtur Kuriatet. Fati i luftës, me miratimin e dy mbretërve, iu besua këtyre. Me sulmin e parë u vranë dy nga vëllezerit Romake si dhe u plagosën lehtë edhe tre vëllezërit Albanë. Horaci, i vetëm, ndonëse ishte jo i plagosur nuk guxoi të vijonte luftimin i vetëm, sepse ishte i pabarabartë një me trë, por duke bërë një arrati te rreme, i vrau një nga një armiqtë që e ndoqën pas. Më pas u kthye në Romë, ngarkuar më prenë e luftës, kur hasi me motrën, e cila ishte e fejuar me një nga Kuriatët. Kur vajza pa mbi supet e vëllait mantelin e të fejuarit - të cilin e kishte punuar vete - nisi të shkulte flokët dhe të vajtonte për djaloshin që ia kishin rrëmbyer. ', 6.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 7);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123523, './Images/218.jpg', 'Sociolinguistika ', '2024-05-30T16:24:00.024Z', 443, 'Vepra Sociolinguistika është përmbledhje e disa studimeve të kryera gjatë një perjudhe 16 vjeçare për çështje që lidhen me kategori e dukuri sociolinguistika. Janë variante të plota të këtyre studimeve , prej të cilave janë nxerrë kumtestat e mbajtura në konferencat e simpoziume komtare e ndërkomtare si dhe ligjeratat e mbajtura me studentët e ciklit të parë , të dytë , të tretë . ', 16.50, '2024-05-30T16:24:00.024Z', 'Paper', 51, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123352, './Images/30.jpg', 'Jeta E Një Historiani', '2024-05-30T16:24:00.024Z', 464, 'Libri përmban nota autobiografike, duke ruajtur në këtë autobiografi fijen lidhëse me familjen e Frashërllinjve të Rilindjes Kombëtare, nga rrjedh edhe akademiku Kristo Frashëri. Në promovimin e librit, që do të mbetet si epilogu i një historiani të lavdishëm shqiptar, pritet një takim mbresëlënës me kolegët dhe gazetarët. Tërë jetën akademik Kristo Frashëri shkroi libra historike, artikuj, ese, dha intervista për çështjet më të debatueshme kombëtare, pa e vënë re asnjëherë lodhjen dhe së fundi as stërmundimin, ndërsa është në një gjendje shëndetësore ku i duhet asistencë mjekësore. Ai e sfidon edhe sëmundjen. Brenda tij gjendet një energji e jashtëzakonshme e mendjes. Të mos mendojmë sot se profesor Kristo Frashëri do të përfundojë së botuari, duke e mbyllur me “jeta e një historiani”. Kemi mësuar se dhjetëra vëllime presin ende dritën e botimit. “Jeta e një historiani”, ky libër i shkruar nga dora e Kristo Frashërit nuk është se flet për veten. Jeta e një historiani si Kristo Frashëri është një betejë për historinë e vendit të tij, një beteje për t’i dhënë ringjallje, për të ndriçuar të vërtetat historike. ', 6.00, '2024-05-30T16:24:00.024Z', 'Paper', 44, 5);
