INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123442, './Images/103.jpg', 'Nje Dekad E Mendimit Politik ', '2024-05-30T16:24:00.024Z', 551, ' Ky tekst, trajton tema nga fushat e prekshme të jetës, me një stil e fjalor të thjeshtë edhe për lexuesit joekonomist, me dëshirë që ai t’i kuptojë sa më thjesht gjërat që për nga vet natyra janë komplekse. Të gjitha analizat mbi bazë të fakteve e argumente të nuk janë deshira buleveardesh të një “ekonomisti popullor”, por të bazuara në metodologji konzistente dhe kohezive të trajtimit shkencor. ', 9.00, '2024-05-30T16:24:00.024Z', 'Paper', 59, 3);
