INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123572, './Images/275.jpg', 'Origjina ', '2024-05-30T16:24:00.024Z', 616, ' ROBERT LANGDON, profesor i simbologjisë dhe i ikonologjisë në Harvard, mbërrin në muzeun ultramodern Gugenhajm të Bilbaos për të qenë i pranishëm në një njoftim madhor – paraqitjen e një zbulimi që “do të ndryshojë përgjithmonë fytyrën e shkencës”. Prezantuesi i mbrëmjes është Edmond Kirsh, një miliarder dhe futurist dyzetvjeçar, të cilin, zbulimet mahnitëse në teknologjinë e lartë dhe parashikimet e guximshme e kanë bërë një figurë të famshme globale. Kirsh, që kishte qenë një nga studentët e parë të Langdonit në Harvard para dy dhjetëvjeçarësh, po bëhet gati të shpallë një zbulim të madh të habitshëm – një zbulim që do t’u japë përgjigje dy prej pyetjeve fundamentale të ekzistencës njerëzore. ', 15.95, '2024-05-30T16:24:00.024Z', 'Paper', 65, 14);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123574, './Images/277.jpg', 'Zogu Ogurzi ', '2024-05-30T16:24:00.024Z', 401, 'Fjelbaka po lë pas krahëve një tjetër dimër të heshtur. Në këtë qytezë dikush kërkon të tërheqë vëmendjen në këtë qendër të vogël të bregdetit. Cili do të ishte një rast më i mirë se një “reality show” me telekamera të vendosura gjithkund për të marrë vende dhe persona që do të hyjnë në shtëpitë e dhjetëra mijëra telespektatorëve? Propozimi është miratuar, dritat janë ndezur por mbërritja e trupës krijon jo pak rrëmujë. Madje producenti, i bindur se skandalet rrisin audiencën, argëtohet duke ushqyer tensionet mes konkurentëve.  ', 11.50, '2024-05-30T16:24:00.024Z', 'Paper', 65, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123459, './Images/138.jpg', 'Magjiologjia ', '2024-05-30T16:24:00.024Z', 88, 'Jeni të etur të mësoni artin misterioz të magjisë? Do të jetë Merlini, dora vetë që do t’jua mësojë atë. Ky libër që ju mëson truke magjike, vjen nga viti 1588, për t’ju treguar ato sekrete që nuk e dinit as që ekzistonin. Cila është puna e një magjistari? Një pamje e plotë dhe me shpjegime të hollësishme e punishtes së tij, veglat e magjistarit, cilat janë yshtjet dhe si funksionojnë ato (që do t’i gjeni të shpjeguara edhe në libërthat që ndodhen brenda librit), cilat janë kafshët magjike dhe shumë e shumë kureshti të tjera mahnitëse. Në libër do të gjeni një sërë simbolesh që fshehin kode që duhen deshifruar, një top kristali të shndritshëm, një hartë botërore të vendndodhjes së magjistarëve, libërtha që shpjegojnë se si mund të përdoren formulat magjike, një dragua të lëvizshëm që mund ta mbani në qafë dhe që ju ndihmon të gjeni kafshët magjike, një pako me tetë karta për të zbuluar të ardhmen, puplën e feniksit dhe së fundi, një mesazh të Merlinit, posaçërisht për ju! ', 24.90, '2024-05-30T16:24:00.024Z', 'Paper', 28, 11);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123460, './Images/139.jpg', 'Libri I Shpikjeve Te Medha ', '2024-05-30T16:24:00.024Z', 122, '100 shpikje që ndryshuan botën, që nga shpikja e rrotës e deri te videolojërat. Një libër i pazakontë që na njeh me shpikjet që kanë bërë histori, duke na treguar se edhe zbulimi më i vogël mund t’u hapë rrugë shpikjeve të rëndësishme. Do të mahniteni kur të mësoni se si kanë lindur idetë e mëdha dhe si u zhvilluan ato, që nga makinat e avionët, te shtypi i shkruar, që nga llamba elektrike, tek antibiotikët, nga kitara elektrike, te xhinset. Libri është i ndarë në pesë kapituj, që grupojnë shpikjet e pesë fushave. Çdo shpikje trajtohet në thellësi: si lindi, cilat janë shpikjet e tjera të ngjashme me të, si e ndryshoi botën ajo, si funksionon, si dhe për cilat shpikje të mëtejshme e hapi udhën. Informacioni është i përmbledhur dhe i trajtuar thjesht, që fëmijët ta përthithin me lehtësi. ', 12.95, '2024-05-30T16:24:00.024Z', 'Paper', 28, 9);
