INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355474, './Images/344.jpg', ' Interneti Dhe Shoqeria', '2024-05-30T16:24:00.024Z', 405, 'Ekziston një distancë (shumë të tjerë përdorin edhe termin “humnerë”) gjithnjë në rritje , midis evulucionit teknologjik dhe atijë social-kulturor. Ështe pikërisht ky boshëllyk ai që do të shkaktojë gjithnjë pabarazi të mëdha , aq sa në gjuhen e ekonomisë , atom und të prodhojnë efekte të dëmshme e të paimagjinuara në ekonomitë tradicionale . Nëse mbetesh prape në këtë sfide socio-kulturore , rrështitja e një vendi në zhvillim e sipër në një vend të botës së tretë ose të katërt është shumë më e shpejtë dhe më katastrofale se më parë . ', 12.90, '2024-05-30T16:24:00.024Z', 'Paper', 45, 14);
