INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355684, './Images/388.jpg', 'Eskili Ne Proze ', '2024-05-30T16:24:00.024Z', 180, 'Leximi i poezisë dramatike të Eskilit, Sofokliut dhe Euripidit, që shijohen e studiohen prej shekujsh në mbarë botën, kërkon përgatitje kulturore dhe njohje të mitologjisë e të historisë antike, çka arrihet me mund dhe në një moshë të caktuar. Por në traditën letrare të botës, për të përqasur edhe të vegjlit me kryeveprat e lashtësisë, për tua bërë të lehtë hyrjen në mrekullinë e poetëve të pavdekshëm, për të zgjuar kënaqësi estetike dhe formim artistik, përdoret paraqitja në prozë, siç ka bërë me besnikëri, me shumë shije, freski dhe përkushtim Janis Benekos. ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 62, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355347, './Images/377.jpg', 'Te Flasim Per Futbollin Platini ', '2024-05-30T16:24:00.024Z', 436, ' Një forcë më shtynte drejt futbollit, i cili do të bëhej qëllimi i jetës sime. Duhet të shtoj: i të gjithë ekzistencës sime. Lojtar, imazh reklamash, njeri i medias, seleksionues, konsulent, president i Kupës së Botës, president i UEFA – s dhe kjo nuk është e gjitha. Kjo është forca ime, qëllimi im: shënimi i golit. Të shënosh një gol në fushën e lojës dhe, përtej kësaj, në fushat e betejës së jetës. ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 58, 7);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355385, './Images/389.jpg', 'Nata Dhe Cdo Te Beje Me Kete Liber ', '2024-05-30T16:24:00.024Z', 200, ' Shkrimeve në prozë e atyre poetike, që i dhuruan famën, Saramago u vë përkrah shkrimet dramatike, ku procesi krijues u nënshtrohet po të njëjtave parime, që mbajnë më këmbë pjesën tjetër të prodhimtarisë së tij, kjo e fundit e bazuar në ndërthurjen mes kohës historike e kohës së trillimit.', 6.00, '2024-05-30T16:24:00.024Z', 'Paper', 30, 10);
