INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123407, './Images/85.jpg', 'Tirana E Nones ', '2024-05-30T16:24:00.024Z', 333, ' Ato ishin tri plaka që më shënjuan fëmijërinë. Investuan gjithçka iu kishte mbetur nga dekada të trazuara të jetës, te një fëmijë; rrëfimet e mrekullueshme për qytetin, pushtetin, fuqinë, dështimin, modestinë dhe zvetënimin. Sot mendoj se kishin nevojë t’ia thoshin dikujt. Ishte një lloj borxhi që e mora përsipër, pa e ditur. Me besimin se një ditë do ua tregoja sa më shumë njerëzve. Dhe po e bëj me këtë libër. Kjo histori është kaq magjepsëse, kaq ndryshe, por edhe kaq e njohur njëherazi ', 11.00, '2024-05-30T16:24:00.024Z', 'Paper', 51, 67);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123524, './Images/219.jpg', 'Hyrje Ne Teorite E Medha Te Romanit ', '2024-05-30T16:24:00.024Z', 304, ' Sot, ka një pandehmë të përgjithshme se romani po përjeton një krizë identitare. Sado që unë nuk pajtohem me këtë pohim, më duhet ta përdor për të theksuar një tjetër kualitet të tekstit të Chartier: Tregon sesi romani nuk është në krizë dhe se identiteti i vetëm i vërtetë i romanit, është të qenit i padefinueshëm qartë. Prandaj, teksti i tij, me të gjitha standardet, përbën një përmbledhje teorike shumë të nevojshme dhe një hyrje të njëmendtë në teoritë e romanit. Kjo vlen veçanërisht për studentët dhe studiuesit e rinj shqiptarë që duhet të çlirohen nga pozat, ashtuqë të bëjnë letërsi të mirëfillit.', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 3, 19);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123542, './Images/241.jpg', 'Qytetet Dhe Qytezat Ilire 1 ', '2024-05-30T16:24:00.024Z', 558, 'Qyteti ilir dokumentohet që në shekujt e 6/5të p.e.s. Hekateu (550-476 p.e.s.) përmend qytetet Adria,Iapygia, Oidantion, Orikos, qytetet kaone Buthrot dhe Baiake dhe qytetin taulant Sesareth. Skylaksi (shek. i 6të p.e.s.), referon qytetet liburne Idassa, Attienites, Dyyrta, Aloupsoi, Olsoi, Pedetai, Hemionoi, qytetet neste dhe enkeleje Buthoe dhe Rizon dhe qytetet taulante/orike Amantia dhe Orikon. Më tej, Thukididi (460-400 p.e.s.) përmend qytetin e Epirit Myon dhe Herodori kryeqendrën e federatës së kasopëve, Kassope. Përmbledhtas, katër autorë të shek. të 6/5 të p.e.s. përmendin 17 qytete të shpërndara njëtrajtësisht në hapësirën ilire, nga skaji më verior, gadishulli i Istrias, në skajin më jugor, gjirin e Ambrakisë. ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 44, 17);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123576, './Images/279.jpg', 'Gurskalitesi ', '2024-05-30T16:24:00.024Z', 532, ' Qyteti i izoluar i Fjelbakës ka parë jo pak tragjedi, por ndoshta asnjë aq të tmerrshme sa ajo e vajzës së vogël të gjetur në rrjetën e një peshkatari. Dhe kjo nuk ishte një mbytje aksidentale… Detektivi i zonës, Patrik Hedstromi, sapo është bërë baba. Është detyra e tij e vështirë të zbulojë se kush qëndron mbrapa vdekjes së një fëmije që, si ai, po ashtu edhe partnerja e tij, Erika, e njihnin mirë. Por ai nuk e di se kjo çështje do të depërtojë në zemrën e errët të Fjelbakës dhe do ta grisë ndoshta përgjithmonë fasadën e saj idilike…', 10.90, '2024-05-30T16:24:00.024Z', 'Paper', 56, 12);
