INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123516, './Images/211.jpg', 'Sistemi Rasor I Emrave Te Shqipes ', '2024-05-30T16:24:00.024Z', 472, '"Sistemi Rasor I Emrave Të Shqipes" nga Kolec Topalli është një studim i detajuar dhe i specializuar mbi sistemin emëror të gjuhës shqipe. Kolec Topalli, një linguist dhe studiues i njohur shqiptar, në këtë vepër shqyrton në mënyrë të thelluar dhe sistematike emrat në gjuhën shqipe, duke analizuar strukturën, funksionet dhe përdorimet e tyre. ', 9.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 18);
