INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123457, './Images/136.jpg', 'Enciklopedia Ime 6 10 Vjec ', '2024-05-30T16:24:00.024Z', 172, 'Këtu gjen të mbledhura së bashku temat kryesore të dijes, për të kuptuar gjithçka për botën rreth nesh! UNIVERSI / TRUPI I NJERIUT / HISTORIA / TOKA / BIMËT / KAFSHËT / BOTA SOT / ARTET / SHKENCA DHE TEKNIKA. Me qindra ilustrime të ndryshme, që kureshtarët e vegjël të zbulojnë botën rreth tyre. Libri është për moshat 6-10 vjeç ', 19.00, '2024-05-30T16:24:00.024Z', 'Paper', 13, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123458, './Images/137.jpg', 'Piratologjia ', '2024-05-30T16:24:00.024Z', 87, 'Ç’do të ndodhte nëse në det do të gjendej një sënduk, i cili duke qenë i izoluar me katran, ka mbetur i pacenuar për shekuj me radhë? Po nëse brenda tij do të gjendej ditari i kapiten Uilliam Luberit, gjuajtës piratësh? Do të ndodhte që shumë mistere të piratëve do të zbuloheshin dhe një jetë e panjohur që mban era thesar do të vinte gjer në ditët tona, me një magji të pamohueshme, nën titullin “Piratologjia”. Libri që është shitur në të gjithë botën në më shumë se 5 milionë kopje, do t’ju çojë në shekullin XVIII, atëherë kur kapiten Luberi ishte vënë në ndjekje të Tmerrit të Deteve, pirates Arabela Drumond. Në këtë udhëtim do të përballeni me stuhitë, ndërsa lundroni drejt ishujve të thesarit, nën flamurin pirat. Një libër i jashtëzakonshëm për piratologët, që ka në brendësi informacione të shumëllojshme për këtë botë joshëse të së shkuarës. ', 23.00, '2024-05-30T16:24:00.024Z', 'Paper', 28, 10);
