INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123347, './Images/24.jpg', 'Antropologjia Politike', '2024-05-30T16:24:00.024Z', 114, 'Libri që mban titullin “Antropologjia politike” i autorit Gjon Keka, para së gjithash duhet parë si një përpjekje e guximshme dhe serioze e autorit për të hapur një perspektivë të re të mendimit politik te ne. Teksti i tij mund të lexohet edhe si një sugjerim i duhur që kurikulat e sociologjisë dhe të shkencave politike te ne të pasurohen me dimensionin e domosdoshëm të antropologjisë politike. ', 4.40, '2024-05-30T16:24:00.024Z', 'Paper', 21, 22 );
