INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123536, './Images/235.jpg', 'Historia E Austrise ', '2024-05-30T16:24:00.024Z', 401, 'Kjo histori e Austrisë shtrihet në harkun kohor që zë fill me periudhën romake, vijon me krijimin e vend-banimeve në Mesjetën e hershme, format e reja të organizimit si krahinë kufitare (markë) kundër avarëve e hungarezëve dhe, përmes Dukatës Babenberge dhe “Shtetit të Alpeve” të Habsburgëve, i cili që nga shekulli i 16 u rrit duke u bërë një fuqi europiane, arrin te Perandoria e Austrisë, Monarkia e dyfishtë Austro-Hungareze dhe së fundi te Republika e vogël e shekullit të 20. ', 15.00 , '2024-05-30T16:24:00.024Z', 'Paper', 66, 17);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123537, './Images/236.jpg', 'Levizja E Lirise ', '2024-05-30T16:24:00.024Z', 4.00, ' Ky libër i Belës, me titull “Lëvizja e lirisë” dhe nëntitull “Lëvizja Studentore e vitit 1997 nën vëzhgimin e mediave”, ka pesë kapituj, në të cilët autori ka ndjekur, hap pas hapi, idenë e realizimit të protestës së organizuar nga Unioni i Pavarur i Studentëve të Universitetit të Prishtinës në vitin 1997, pastaj raportimet e mediave vendore dhe ndërkombëtare për jehonën e asaj ngjarjeje, por edhe të disa të tjerave që ndodhën pas asaj proteste në Prishtinë. ', 4.00, '2024-05-30T16:24:00.024Z', 'Paper', 12, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123589, './Images/293.jpg', 'Fjalor I Muzikes Klasike Dhe Popullore ', '2024-05-30T16:24:00.024Z', 287, 'Përfshin 3000 zëra dhe 2 mijë vjet muzikë të shkruar në Europë dhe Shqipëri. Fjalori i parë terminologjik i muzikës klasike dhe popullore" mban firmën e Ilir Polenës dhe Vaso Toles. Në këtë botim janë të përfshirë rreth 3000 fjalë. Tole thotë se puna për këtë fjalor është nisur nga ky dorëshkrim i studiuesit të njohur Ilir Polena që 20 vite më parë dhe botimin e cilëson si amanet të përmbushur, pasi ai është ndarë nga jeta një vit më parë. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 44, 8);
