INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355628, './Images/334.jpg', 'Kalifja ', '2024-05-30T16:24:00.024Z', 272, ' Një vajzë e thjeshtë, por shumë e ëmbël, Kalifja, bëhet e dashura e të fuqishmit të qytetit, industrialistit më të madh, Mastro don Gesualdo.Ajo është portreti i paharrueshëm i një gruaje të lirë, të guximshme, instiktive, rebele, e paaftë për kompromise, zemërgjerë, pra, në fund të fundit, në mënyrën e saj të pafajshme.Një dashnore pa servilizëm, në pastërtinë dhe dashurinë e të cilës industrialisti do të rigjejë një dëshirë të re për jetën dhe vetë lirinë.', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 31, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355329, './Images/335.jpg', ' Serenata', '2024-05-30T16:24:00.024Z', 472, ' Fundi i viteve 30... Maksimilian Vagner, një gjerman me gjak të pastër arian dhe pedagog në universitet, bie në dashuri me vajzën e bukur hebreje të quajtur Nadia. Pas martesës, për të fshehur identitetin hebre të Nadias, ajo mori emrin Deborah... ', 12.95, '2024-05-30T16:24:00.024Z', 'Paper', 42, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123583, './Images/286.jpg', 'Zoteria I Jetimeve ', '2024-05-30T16:24:00.024Z', 546, ' Pak Jun Do është bir i një nëne të zhdukur - një këgëtareje të rrëmbyer dhe të dërguar në Penian, për të joshur të fuqishmit e kryeqytetit - dhe i një babai të rreptë, drejtor i një jetimoreje. Në moshë të rritur, shquhet për besnikërinë dhe guximin, aq sa e bindi shtetin te i japë mundësinë të ngjitë me shpejtësi shkallët e karrierës. Kështu i hynë kësisoj një rruge pa kthim, përmes kthinave të fshehta të diktaturës më misterioze të planetit. ', 10.70, '2024-05-30T16:24:00.024Z', 'Paper', 14, 14);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123584, './Images/287.jpg', 'Misteri I Trenit Blu ', '2024-05-30T16:24:00.024Z', 298, ' Një tren shumë i bukur përshkon hekurudhën që lidh Londrën gri me plazhet e ngrohta të rivierës franceze; është Treni Blu, treni i multimiliarderëve, në të cilin nuk mungojnë skandalet dhe pasuria, por edhe ku krimi mund të nxjerrë kokën pas çdo dere. Pikërisht në njërin prej vagonëve kryhet një vrasje djallëzore teksa agresori përpiqet, me sukses, të grabitë një varëse të çmuar prej rubini. Ruth Keteringu, bija e miliarderit Van Aldin dhe gruaja e një lordi anglez, mbytet në mënyrë brutale teksa udhëtonte me tren. ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 66, 17);
