INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123373, './Images/50.jpg', 'Si Ta Kuptosh Arkitekturen', '2024-05-30T16:24:00.024Z', 288, ' Keni menduar ndonjëherë për shtëpinë tuaj, zyrën, shkollën, kinemanë, gjellëtoren, dyqanet, rrugët dhe sheshet ku shkoni shpesh? I keni parë ndonjëherë hapësirat brenda të cilave jetoni? Keni reflektuar mbi vlerën e veçantë të arkitekturës, krahasuar me atë të arteve të tjera figurative? Çfarë ndryshimi ka mes banesës suaj dhe një tempulli apo një harku triumfi? Po mënyra se si e gjykojmë një monument të Bramantes ose të Borrominit, a bazohet në kritere të ndryshme nga ato mbi të cilat mbështetet vlerësimi i një vepre të Terragnit, le Corbusier-së ose e Wrightit? A është arkitektura një art “abstrakt” apo ka përmbajtje të qartë? Si ta kuptosh arkitekturën u përgjigjet këtyre pyetjeve; ka për qëllim të zbulojë sekretin, thelbin hapësinor të arkitekturës, në mënyrë që edhe ju të dini t’i shikoni mjediset ku shpenzoni një pjesë të madhe të ekzistencës suaj.', 12.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 5);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123526, './Images/221.jpg', 'Vitet E Fshehura ', '2024-05-30T16:24:00.024Z', 266, 'Poezia e Rexhep Hotit krijohet me vargun e vecantë që bartë në vete botën e mendimit poetic, me emocione që shëtisin lirshëm nëpër epoka, stinë e hapësira tej mitologjike, duke i përthyer meditimet dhe përsiatjet e heroit lirik deri në pafundësinë e universit për t’ia sjell botës së brendshme të përjetimit pasurinë e artit me nxitje, çaste e ngjyrime origjinale. Filozofia estetike e krijimit të tij është metafora e nënshtresuar në përmasa të frikshme të së bukurës. Poezia e Rexhep Hotit e ka dhuntinë për ta ndarë me lexuesin përjetimin e mesazhit artistik. Ajo rrëmben në udhëtimin e saj pa e kuptuar se je bërë pjesë e aventurave të vargut, të shkronjave, të fjalëve të saj, çfarëdo qoftë stina apo hapësira ku ajo të hedhë. ', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 63, 15);
