INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123544, './Images/243.jpg', 'Komploti Kunder Lirise ', '2024-05-30T16:24:00.024Z', 650, ' Shqipëria nga diktatura në demokracinë bolshevike si dhe nga diktatura e proletariatit pa proletarë, në kapitalizmin borgjez pa borgjezi. Konflikti mes demokracisë dhe lirisë. Pushteti i drejtësisë pa drejtësi. Korrupsioni dhe krimi i pandëshkuar: piramidat financiare, grushtet e shtetit deri te tragjedia e Gërdecit dhe masakra e 21 janarit. ', 14.95, '2024-05-30T16:24:00.024Z', 'Paper', 3, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123411, './Images/90.jpg', 'Momentet Vendimtare ', '2024-05-30T16:24:00.024Z', 506, 'Libri që shpresoj të pasqyrojë një tablo të tetë viteve te njëpasnjëshme të presidencës. Besoj se është e pamundur të arrihet në përfundime të prera rreth presidencës sime – qoftë edhe presidencës së mëparshme – për disa dekada. Me kalimin e kohës pasionet fashiten, rezultatet qartësohen dhe për pasojë, studiuesit e kanë më të lehtë të krahasojnë qasje të ndryshme.  ', 20.00, '2024-05-30T16:24:00.024Z', 'Paper', 53, 9);
