INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (1235534, './Images/311.jpg', 'Libri I Psikologjise ', '2024-05-30T16:24:00.024Z', 352, 'A jemi vërtet ndryshe nga të tjerët apo u përshtatemi atyre? Pse kujtojmë dhe pse harrojmë? Si mund ta matim inteligjencën? Këto pyetje e të tjera syresh janë thelbi i punës së disa prej mendimtarëve dhe eksperimentuesve më të mëdhenj në botë në këtë fushë magjepsëse. Libri i Psikologjisë është plot me shpjegime që depërtojnë në thelbin e kuptimeve të ndërlikuara, i pajisur me diagrame që zbërthejnë teori të koklavitura, me citime të paharrueshme dhe ilustrime të goditura, të cilat luajnë me perceptimet dhe besimet tona. ', 24.95, '2024-05-30T16:24:00.024Z', 'Paper', 62, 17);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (1235545, './Images/312.jpg', 'Terapia E Mevlana Xheladin Rumiut ', '2024-05-30T16:24:00.024Z', 401, 'Mevlana do të jetë ai i cili do të na tregojë rrugën nga epoka e dijes në epokën e urtësisë. Ai na prek butonat e shifrave të strukturës sonë shpirtërore dhe e fut në lëvizje ndjesinë e cila ekziston te ne. -Prof. Dr. Nevzat Tarhan Njerëzimi po rizbulon Mevlanën. Mësimet e tij janë tepër universale për të mbetur të mbërthyera në kohën që jetoi ai, sepse të gjithë kemi shumë për të mësuar prej tij. Duke parë nga këtu, prof. dr. Nevzat Tarhan shpjegon se si urtësia e Mesnevi, e cila i tejkaloi shekujt mund të jetë shëruese për shpirtin. ', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
