INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123461, './Images/140.jpg', 'Libri Im I Eksperimenteve ', '2024-05-30T16:24:00.024Z', 130, ' 101 eksperimentet shkencore të këtij libri, që mbështeten në tema të tilla, si: ajri dhe gazi, magnetët, ngjyrat, shqisat, elektriciteti e të tjera, do t’jua rrëfejnë ligjet e shkencës në praktikë, që ju t’i zbatoni me duart tuaja. Për ta bërë shkencën argëtuese, nuk ka mënyrë më të mirë sesa t’i realizoni këto eksperimente të thjeshta në kushtet e shtëpisë, me mjetet që keni përqark. Ky koleksion i pasur eksperimentesh do t’i argëtojë pa fund kureshtarët, duke u treguar kështu mjaft gjëra për shkencën dhe ligjet e natyrës, në mënyrën më të thjeshtë dhe më argëtuese. E kush nuk do të donte të shndërrohej në një Ajnshtajn të vogël, të mësonte të krijonte një ylber, një vullkan nënujor apo të ndërtonte një busull? Çdo eksperiment në libër shpjegohet thjesht e qartë, si edhe shoqërohet me foto të hollësishme, që do t’jua bëjnë punën shumë të lehtë. Zbulojeni këtë botë të mahnitshme!', 11.95, '2024-05-30T16:24:00.024Z', 'Paper', 28, 10);
