INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123447, './Images/108.jpg', ' Politika Financiare E Ndermarrjes', '2024-05-30T16:24:00.024Z', 202, 'Politika financiare e ndërmarrjes paraqet një kuadër të analizës teorike të nëvojshme për të interpretuar problemet e financës në brendësi të ndërmarrjes dhe faktorët që përcaktojnë zgjedhjet e politikave financiare, duke u ndalur në rastin e ndërmarrjes agroindustriale. ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 31, 14);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123414, './Images/93.jpg', 'Zonjat E Para ', '2024-05-30T16:24:00.024Z', 324, 'Një shikim i imtësishëm në jetën e zonjave të para të Amerikës moderne, që nga Xheki Kenedi, Nensi Regani e deri te Mishel Obama.Detyra e zonjës së parë të Amerikës nuk është shumë e vlerësuar, megjithatë prej tyre kërkohet kaq shumë: ato duhet të jenë udhëheqëse frymëzuese; duhet të planifikojnë më së miri çdo lëvizje apo aktivitet; duhet të jenë gjithaq politikane të mira sa bashkëshortët e tyre, që kanë në dorë timonin e gjithë botës; duhet të jenë bashkëshorte dhe nëna që i kryejnë këto dy detyra nën qindra sy vëzhgues dhe kritikë; si edhe drejtuese të afta të rezidencës së Shtëpisë së Bardhë.Ashtu si në librin e saj të parë, “Rezidenca - brenda botës private të Shtëpisë së Bardhë”, edhe këtë herë, autorja Kate Andersen Brower sjell për lexuesit në mbarë botën detaje të brendshme, të marra nga stafi i ngushtë presidencial, por edhe nga vetë zonjat e para të Amerikës. ', 13.00, '2024-05-30T16:24:00.024Z', 'Paper', 52, 19);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123392, './Images/69.jpg', 'Shqipëria Në Art ', '2024-05-30T16:24:00.024Z', 341, ' Shqipëria në art, paraqitet si një enciklopedi e kujtesës figurative për historinë, kulturën dhe trashëgiminë kombëtare. Përmes statujave, mozaikëve, afreskeve, pikturave, gravurave, litografive, grafikave, akuareleve, të krijuara nga kohët më të hershme deri në ditët tona, njihemi me aspekte nga më të ndryshmet të jetës në trojet e banuara prej shqiptarëve. ', 49.40, '2024-05-30T16:24:00.024Z', 'Paper', 3, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123327, './Images/256.jpg', 'Endërr', '2024-05-30T16:24:00.024Z', 136, ' Një eksplorim surreal i përmasave intime të martesës. Kurioziteti i sinqertë i Fridolinit për seksualitetin e bashkëshortes nuk i kundërvihet dot xhelozisë dhe narcizizmit të tij të lënduar. Schnitzler – i pyet veten sesi dy njerëz të zgjuar, tërheqës dhe të ndjeshëm mund të ndajnë një shtrat, një fëmijë dhe një jetë për vite me radhë, kur dëshirat e zjarrta erotike, të drejtuara gjetkë, janë të pashmangshme.Duket se përgjigja qëndron te gënjeshtra, në një balancë sekretesh midis dy të huajsh. Vullneti i tyre për të hetuar fantazitë e kaluara dhe të tashme të njëri – tjetrit e bën martesën e tyre të pazakontë. Nëse ky origjinalitet e afron apo jo shkatërrimin e çiftit, kjo mbetet për tu zbuluar.Novela "Ëndërr" ka frymëzuar regjisorin e njohur Stenley Kubrick për realizimin e "Eyes wide shut" me aktorët Nicole Kidman dhe Tom Cruise ', 6.00, '2024-05-30T16:24:00.024Z', 'Paper', 7, 9);
