INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355377, './Images/347.jpg', 'Bazat E Programimit Kompjuterik ', '2024-05-30T16:24:00.024Z',224, 'jë udhëzues unik, i rrallë, i pari në shqip, për të apasionuarit pas programimit kompjuterik, si dhe për të gjithë ata që duan të kuptojnë se si funksionojnë kompjuterat. Programi kompjuterik është tërësia e udhëzimeve që zbaton kompjuteri për të kryer një detyrë. “Të kodosh” ose “të programosh”, do të thotë të shkruash hap pas hapi udhëzime, të cilat i tregojnë kompjuterit se çfarë duhet të bëjë. ', 14.00, '2024-05-30T16:24:00.024Z', 'Paper', 28, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123451, './Images/113.jpg', 'Politika Financiare E Ndermarrjes ', '2024-05-30T16:24:00.024Z', 401, 'Politika financiare e ndërmarrjes paraqet një kuadër të analizës teorike të nëvojshme për të interpretuar problemet e financës në brendësi të ndërmarrjes dhe faktorët që përcaktojnë zgjedhjet e politikave financiare, duke u ndalur në rastin e ndërmarrjes agroindustriale. ', 7.20, '2024-05-30T16:24:00.024Z', 'Paper', 31, 8);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123408, './Images/87.jpg', ' E Verteta Ime', '2024-05-30T16:24:00.024Z', 401, '"Për herë të parë, Hillari Klinton rrëfen përjetimet e saj gjatë një prej zgjedhjeve më kontroverse dhe të paparashikueshme në histori. Tanimë, e lirë nga çdo trysni, ajo flet për një garë presidenciale të mbushur me zemërim, seksizëm, ulje-ngritje të pazakonta dhe një kundërshtar, i cili shkeli të gjitha rregullat e garës.Në këtë libër, ajo tregon se ç’do të thotë të garosh kundër Donald Trampit, cilat ishin gabimet e saj, si e përballoi ajo atë humbje shkatërruese dhe si gjeti fuqi të mblidhte veten e të shihte përpara për të ardhmen; ç’sfida duhet të kalojë një grua për t’u shfaqur e fortë në sy të publikut të gjerë, kritikat për zërin, moshën dhe pamjen e saj, por, mbi të gjitha, për standardet e dyfishta me të cilat përballet një grua në politikë.Zgjedhjet e vitit 2016 në Amerikë u shënjuan nga një sulm i paprecedent në demokracinë e vendit, nga një kundërshtar i huaj. ', 14.00, '2024-05-30T16:24:00.024Z', 'Paper', 53, 58);
