INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355325, './Images/331.jpg', 'Nderi I Heshtur ', '2024-05-30T16:24:00.024Z', 386, 'Historia fillon me Hiroko Takashimaya, një vajzë e re japoneze, e cila vjen në Amerikë për të studiuar në një universitet të njohur në Kaliforni. Babai i saj, një profesor prestigjioz, e dërgon atë për të jetuar me xhaxhain e saj dhe familjen e tij në Kaliforni. Megjithatë, jeta e Hirokos ndryshon në mënyrë dramatike pas sulmit të Pearl Harbor-it në vitin 1941, kur SHBA hyn në Luftën e Dytë Botërore dhe japonezët në Amerikë fillojnë të përballen me diskriminim dhe internim. ', 12.95, '2024-05-30T16:24:00.024Z', 'Paper', 9, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355426, './Images/332.jpg', 'Premtim Dashurie ', '2024-05-30T16:24:00.024Z', 401, 'Nga dhoma e Kezias nuk vinte as zhurma më e vogël. Nuk kishte as dhjetë minuta që ishte shtrirë në divan, por ndihej tepër i tensionuar dhe nervoz për të marrë një sy gjumë. Ishte sikur të kishte folur, biseduar, diskutuar ditë të tëra. Frika e tij më e madhe ishte se mos e trembte deri në atë pikë sa ta detyronte të ikte, të bënte ndonjë veprim të gabuar që ta detyronte tE ia përplaste në fytyrë derën e zemrës dhe të shpirtit të saj. Ja pse ishte atje i shtrirë në atë divan dhe u mjaftua vetëm ta puthte në njërën faqe. Kezia nuk ishte prej atyre grave, të cilave mund tu vërsulesh vrullshëm. Njerëz të tillë humbasin që përpara se të sulmojnë. Por gjatë një nate, ama, ata përshkuan një rrugë shumë të gjatë. Duhej të kënaqej vetëm me kaq. ', 14.00, '2024-05-30T16:24:00.024Z', 'Paper', 9, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355438, './Images/368.jpg', 'Shpikja E Tradites ', '2024-05-30T16:24:00.024Z', 400, 'Shumë nga traditat të cilat i mbajmë si me origjinë të lashtë, në fakt janë shpikur relativisht së fundi. Ky libër hulumton disa raste të këtij procesi shpikjeje: krijimi i "kulturës kombëtare" të Uellsit dhe Skocisë; përpunimi i ritualeve mbretërore britanike të shekujve XIX dhe XX; origjina e ritualeve perandorake në Indinë britanike dhe Afrikë; dhe përpjekjet e lëvizjeve radikale për të zhvilluar kundërtraditat e tyre. Ky libër, me shtrirje kaq të gjarë, synon të hetojë ndërveprimin kompleks të së shkuarës me të tashmen, në një studim brilant të ritualit dhe simbolizmit.  ', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 53, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123413, './Images/92.jpg', 'Te Panjohurat E Linkolnit ', '2024-05-30T16:24:00.024Z', 401, 'Përfundimisht, mora zjarr dhe vendosa të shkruaj vetë një libër për Linkolnin. E dija që nuk kisha shtysën, temperamentin, kualifikimin apo aftësinë e nevojshme për të prodhuar një libër me nivel në dobi të studiuesve dhe historianëve. ', 12.00, '2024-05-30T16:24:00.024Z', 'Paper', 54, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123420, './Images/99.jpg', 'Maria Skllodovska Kyri ', '2024-05-30T16:24:00.024Z', 404, ' Maria Skllodovska-Kyri (Maria Sklodoëska-Curie) ishte një personalitet i jashtëzakonshëm. Ajo ishte e para përfaqësuese e gjinisë së bukur, e cila në vitin 1893 mori diplomë në Fizikë dhe Matematikë në Universitetin e Sorbonës të Parisit; ishte e para profesoreshë femër në Sorbone; ishte e para femër dhe e vetme ndër katër personalitete botërore që ka marre dy herë çmimin Nobël. Kjo shkencëtare e shquar me kombësi polake është e para dhe e vetmja femër, njëkohësisht personaliteti i parë me kombësi jo franceze, që prehet në Panteonin e Parisit. Libri i shkrimtares polake Ivona Kienzler, e përshkruan Skllodovska-n, bashkë me zbulimet e saj shkencore, jo vetëm si një shkencëtare të shquar, por edhe si një femër që dashuron, por që pengohet nga i dashuri i saj. Për këtë ajo u detyrua të thyejë stereotipet false dhe për dashurinë ishte e përkushtuar të sakrifikonte shumë gjëra.', 12.00, '2024-05-30T16:24:00.024Z', 'Paper', 3, 48);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123543, './Images/242.jpg', 'Kurpalet ', '2024-05-30T16:24:00.024Z', 288, 'Kurpalet nje liber historik qe te magjeps me pershkrimin e saj historik... ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123575, './Images/278.jpg', 'Plani I Fshehte Mekkormak ', '2024-05-30T16:24:00.024Z', 298, 'Perspektiva e diellit të Mesdheut është joshëse dhe me atë entuziazmin e saj,Tessa i kushton pak vëmendje paqartësisë së babait lidhur me të zotin e shtëpisë, me emrin Grant Sullivan. Gjithsesi, që në çastin kur mbërrin në Nice dhe takohet me Grantin, ajo është e ndërgjegjshme se nuk do të ketë gjëra të zakonshme atë stinë vere. Lidhja e tyre romantike thellohet, por Tessa kupton se do të përballet edhe me gjëra djallëzore, përveç faktit se do të bjerë në dashuri.  ', 5.95, '2024-05-30T16:24:00.024Z', 'Paper', 56, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123587, './Images/291.jpg', 'Une ', '2024-05-30T16:24:00.024Z', 372, 'UNË është e mbushur me dramë, që nga refuzimi i hershëm i punës së Eltonit me partnerin këngëshkrues Berni Topin deri në daljën jashtë kontrollit të superyllit që kryesonte listat e klasifikimit të këngëve; nga miqësia me Xhon Lenonin, Fredi Mërkërin dhe Xhorxh Majkëllin deri te vallëzimi disko me Mbretëreshën; nga drogës, që do ta mbante mbërthyer për me shumë se një dekadë.  ', 13.30, '2024-05-30T16:24:00.024Z', 'Paper', 30, 8);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123366, './Images/41.jpg', 'Trashegimia Arkeologjike E Komunes Se Skenderajt', '2024-05-30T16:24:00.024Z', 205, 'Kërkimet arkeologjike, përnjohjet sistematike të territorit të Komunës së Skenderajt,kanë nisur në vitin 2013 dhe me disa ndërprerje kanë vazhduar deri në mesin e vitit 2017. Ato u kryen nga Instituti Arkeologjik I Kosovës nën drejtimin e autorit dhe po kurorëzohen me botimin e librit “ Trashëgimia arkeologjike e Komunës së Skenderajt" ', 12.00, '2024-05-30T16:24:00.024Z', 'Paper', 32, 37);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123525, './Images/220.jpg', 'Antologji E Poezise Suedeze ', '2024-05-30T16:24:00.024Z', 649, 'Në Antologjinë e poezisë suedeze përfshihen 100 poetë suedez , me rreth 550 poezi , në shtrirje kohore prej tre shekujsh ( 1700 – 2000) . Poezitë e përzgjedhura dhe të përkthyera këtu paraqesin vlerat e pa kontestueshme letrare , pjesa dërrmuese janë perla të poezisë suedeze në përgjethsi. ', 9.95, '2024-05-30T16:24:00.024Z', 'Paper', 39, 18);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123412, './Images/91.jpg', 'Liri Belishova Dhe Koha E Saj ', '2024-05-30T16:24:00.024Z', 243, ' Nëse do të përpiqeshim ta katalogonim në një gjini të përcaktuar veprën që sot po i paraqitet lexuesit shqiptar, mund të kishim ndonjë vështirësi në emërtim, gati sui generis. Pena e sigurtë, e konsoliduar, mjeshtërore e shkrimtarit, përkthyesit e estetit letrar Bashkim Shehu, na sjell kësaj radhe të konceptuar në një mënyrë moderne dhe mbase jo fort të rrahur në lëminë e letrave shqipe, një vepër që mbart të gërshetuara sa tiparet e një analize të mirëfilltë studimore të karakteristikave të komunizmit shqiptar e atij ndërkombëtar të ish-kampit socialist, por po aq edhe rrugëtimin biografik tragjik të njërës prej protagonisteve të skenës politike shqiptare të pas vendosjes së komunizmit në Shqipëri, Liri Belishovës. Por, rasti Belishova, është vetëm ilustrimi i një analize të thelluar, shterruese, të kontekstit politik e ideologjik të kohës në Shqipërinë që rrëshqiti shumë shpejt në totalitarizëm. Vepra na vjen si një mozaik i plotë analizash politike, gjeopolitike, përsiatjesh sa filozofike aq edhe letrare e psikanalitike të cilat e kornizojnë, por duke mbajtur në qendër të tyre Belishovën si një ndër shembujt më dramatikë ku mbrojtësja dhe bija e ideologjisë komuniste, shndërrohet në viktimën e saj.', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 51, 20);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123564, './Images/265.jpg', 'Nje Ure Fjalesh Mes Nesh E Tjetrit ', '2024-05-30T16:24:00.024Z', 401, 'Ky libër është një reflektim i thellë mbi marrëdhëniet njerëzore dhe mënyrën se si fjala mund të shërbejë si një mjet komunikimi dhe kuptimi mes njerëzve. ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 51, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123444, './Images/105.jpg', 'Hyrje Ne Biznes ', '2024-05-30T16:24:00.024Z', 783, ' Hapja e dyerve ndaj botës së biznesit përmban shumë sfida, si për pedagogët,ashtu dhe për studentët. Qëllimi i këtij teksti është të sigurojë çelësin e suksesit që do të çojë në një përvojë shpërblyese edukative dhe do të krijojë mjedisin e duhur për të bërë karrierë në biznes. Hyrja në biznes mbulon një hapësirë të madhe, duke prekur të gjitha fushat kryesore funksionale të biznesit. ', 14.00, '2024-05-30T16:24:00.024Z', 'Paper', 51, 45);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123529, './Images/224.jpg', 'Kosova Dhe Lajkat ', '2024-05-30T16:24:00.024Z', 401, '  O nuk të linda për t’u dridhur para armikut , dëgjjohet një brimë misterioze nga thellësirë e tokës , por të linda e të rrita me shira lotësh që të jesh sokol maje shkëmbi , të rrokësh jakë për jakë me dhunuesin , oj preja e ligë e armikut e futa e zezë e kombit . Ke hapur sytë , ke shtrirë duart dhe kërkon të të lirojnë të tjerët . Si mund të ngjajë ajo , kur ti je gjunjëzuar aq poshtërisht nga ethet e frikës duke u shtrirë për tokë si stërvinë e qelbur .', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 32, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123530, './Images/225.jpg', 'Ndre Mjeda ', '2024-05-30T16:24:00.024Z', 124, ' Përvijon portretin letrar të shkrimtarit të madh në 150-vjetorin e lindjes, i cili kaloi nëpër tri formacione stilistike letrare për të mbetur përgjithmonë liriku klasik i shqipes. ', 3.95, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123381, './Images/59.jpg', 'Arti Ne Rezistence ', '2024-05-30T16:24:00.024Z', 809, 'Libri ndahet në tri pjesë. Në fillim jepen analizat socio-politike e viteve ’90 kur është instaluar apartheidi në Kosovë dhe se si është reflektuar shtypja masive në mobilizimin e shoqërisë dhe kulturës kosovare. Teza e Maliqit është se si përgjigje ndaj represionit serb janë krijuar modele inventive të rezistencës që jo vetëm se nuk e lejuan degradimin dhe akulturimin (çka ishte qëllimi i Serbisë) por paradoksalisht, kontribuuan në përparimin dhe avancimin e krimitarisë artistike. Pjesa qëndrore e argumentit të Maliqit është ekspozita e artit bashkëkohor kosovar në Beograd e vitit 1997, e titulluar në gjuhën shqipe „Përtej“, që ka sjell një kthesë historike për artet pamore tona, duke i përafruar ato me artin bashkëkohor botëror. Në pjesën e dytë , Maliqi kronologjikisht sjell kritikat mbi projektet artistike të viteve ’90 të dy rrymave, të atyre moderniste, dhe të atyre postmoderniste ose konceptuale.Shquhen analizat e Maliqit të veprave të Gjelosh Gjokajt, Agim Çavdarbashës, Rexhep Ferrit, Simon Shirokës etj. si dhe të Sokol Beqirit, Mehmet Behlulit, Erzen Shkolollit dhe Zake Prelvukajt. Në pjesën e tretë Maliqi bën një paralaks (hark kohor) që lidh vitin ’74 të shekullit XX, me vitin 2014. Ai boton dy shkrime të veta që i ka shkruar si student, njëri është kritikë filozofike e Xhozef Bojsit, artistit botëror më me influencë i pjesës së dytë të shek XX, që Maliqi e vlerëson si utopist. Por pastaj ky shkrim është temë e një dialogu interesant mes Shkëlzen Maliqit dhe Sezgin Bojnikut, antropologut dhe kritikut të artit nga Prizreni, i cili prej vitesh jeton në Finlandë. Aty shtrohet pyetja se në cilin moment duhet filluar qasja historizuese mbi artin kosovar, p.sh. duke e bërë dallimin mes artit modernist dhe artit bashkëkohor. Është ky një debat që vetëm piketon problemin dhe prek lehtë vetëm parimet e një pune të cilën skena e arteve pamore te shqiptarët ende (s’) e trajton sepse ka ngecje serioze institucionale. ', 14.90, '2024-05-30T16:24:00.024Z', 'Paper', 47, 8);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123416, './Images/95.jpg', 'Spiuni Dhe Tradhtari ', '2024-05-30T16:24:00.024Z', 365, 'Historia më e madhe e spiunazhit të Luftës së Ftohtë. Ben Macintyre, në librin e tij "Spiuni dhe tradhtari" tregon një histori tradhtie, jetë të dyfishtë dhe guximi të madh që arriti ta ndryshojë kursin e Luftës së Ftohtë përgjithnjë. ', 20.00, '2024-05-30T16:24:00.024Z', 'Paper', 21, 19);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123562, './Images/263.jpg', 'Shkrime Kritike ', '2024-05-30T16:24:00.024Z', 383, ' Shkrimet kritike të James Joyce-it, mbulojnë një hark kohor prej më shumë se dyzet vitesh në jetën e autorit, dhe shënojnë ndryshime me rëndësi në mendimin e tij mbi politikën, mbi marrëdhënien mes letërsisë dhe historisë, si dhe mbi shkrimtarë që ndikuan thellësisht tek ai, si: Mangan, Blake, Defoe, Ibsen, Wilde dhe Shaw. Ky është botimi i parë në shqip i shkrimeve kritike të James Joyce-it, një prej autorëve më të rëndësishëm të shekullit XX. ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123563, './Images/264.jpg', 'Traktat Mbi Metriken ', '2024-05-30T16:24:00.024Z', 223, ' Kur tragjikët grekë e zhvilluan prozodinë greke paraprirëse, pasuan forma korale që janë me kuptimin e plotë të ‘’lira’’. Deri sa një superstrukturë terminologjike iu ngjesh atyre prej shqyrtarëve që as Eskilin e as Euripidin s’ishin vënë ndonjëherë ta lexonin. Mbase kjo terminologji do të jetë mbrujtur nga farë e pastër e njeriut që kurrë s’ka pas dëgjuar vargje, e mbase as do të jetë në gjëndjë të bëntë dallimin e ritmit dantesk nga ai miltonian, nëse do t’ia vinin t’i dëgjonte të lexuar me zë të lartë. ', 3.95, '2024-05-30T16:24:00.024Z', 'Paper', 63, 20);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123382, './Images/61.jpg', ' Arti Prej Zanafilles Te Van Gogh', '2024-05-30T16:24:00.024Z', 978, ' Arti, prej zanafillës te Van Gogh" përmbledh rreth 450 vepra të zgjedhura, sidomos të artistëve, që me vizionet e tyre origjinale zgjeruan kufijtë e gjuhës së pikturës. Janë pikërisht ata artistë që mbajnë parasysh edhe larminë, pasurinë dhe veçoritë jo vetëm të periudhës historike, por edhe të artistëve të veçantë, sepse çdo artist meriton trajtim të posaçëm, qoftë si shprehje e një prirjeje të caktuar, por sidomos si bartës të një vizioni të ri të botës. Libri është i lehtë për te u qëmtuar, mjaft praktik si dhe përmban riprodhime të kryeveprave të kësaj periudhe. Shpresa jonë është që edhe lexuesi të mbetet i prekur nga kjo pasuri e madhe...', 20.00, '2024-05-30T16:24:00.024Z', 'Paper', 31, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123539, './Images/238.jpg', 'Mary Edith Durham ', '2024-05-30T16:24:00.024Z', 150, ' Mary Edith Durhami është një mal i lartë, tepër i lartë për te u ngjitur. Të mendosh për veprimtarinë dhe veprën e saj është një tronditje e thellë shpirtërore. Të shkruash sa dhe si duhet është një ndërmarrje e mundimshme, që do shumë kohë. Por nderimi dhe dashuria që ne shqiptarët kemi për të, janë nxitje e frymëzim detyrues. Në pamundësi për te i ngritur një monument, i kushtohet ky libër, në shenjë mirënjohjeje shqiptare, me shumë nderime dhe dashuri. Nga ky libër, shqiptarët do të mësojnë më shumë të vërteta nga historia e tyre, më shumë të dhëna për kombin dhe për vetveten, për qëndrimet e sjelljet e fqinjëve dhe Fuqive të Mëdha, në fillim të shekullit XX.', 7.00 ,'2024-05-30T16:24:00.024Z', 'Paper', 59, 13);
