INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355557, './Images/353.jpg', 'Sapiens Nje Histori E Shkurter E Njerezimit ', '2024-05-30T16:24:00.024Z', 440, 'Gjithëpërfshirës, provokues dhe i guximshëm, Sapiens ka sfiduar gjithçka dinim rreth të qenit njeri: mendimet tona, veprimet tona, fuqinë tonë... dhe të ardhmen tonë. 100,000 vite më parë së paku gjashtë lloje humanësh popullonin Tokën. Sot është vetëm një. Ne. Homo sapiensët. Si ia doli lloji ynë të pushtojë botën? Pse u bashkuan paraardhësit tanë për të krijuar qytete dhe mbretëri? Si filluan të besojnë te perënditë, kombet dhe të drejtat e njeriut? Si do të jetë bota jonë në mijëvjeçarët që do të vinë? ', 20.00, '2024-05-30T16:24:00.024Z', 'Paper', 21, 10);
