INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123592, './Images/296.jpg', ' Estetika E Muzikes', '2024-05-30T16:24:00.024Z', 182, ' Në shekullin XIX estetika ishte sfera më e lartë e mendimit muzikor, ndërsa sot kritikuesit e saj shprehin dyshimin se është një spekulim i zbrazët, tepër larg realitetit muzikor sa të mund ta prekë apo të mund të ndikojë vërtet mbi të. Nga ana tjetër, nuk mund të mohohet se gjykimet mbi muzikën, e madje çdo aktivitet muzikor, janë produkt supozimesh estetike për të cilët duhet domosdoshmërisht të ketë ndërgjegjësim nëse kërkohet që të vihen përballë proves së fakteve, apo të mbahet distance kritike. ', 7.80, '2024-05-30T16:24:00.024Z', 'Paper', 32, 10);
