INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123593, './Images/297.jpg', 'Ditari Roze 2 ', '2024-05-30T16:24:00.024Z', 184, 'Një shembull i bukur i "mangas së re" franceze, ku vështirësitë e adoleshencës dhe ëmbëlsia e dashurisë përzien në mënyrë mjeshtërore me fantazinë stilistike mjaft të hollë të krijueses. ', 4.50, '2024-05-30T16:24:00.024Z', 'Paper', 58, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123594, './Images/298.jpg', 'Ditari Roze 1 ', '2024-05-30T16:24:00.024Z', 192, 'Sioko Tominari, 16 vjeçe, e tronditur, zbulon se djali të cilit katër vjet më parë kishte tentuar dëshpërimisht te i shprehte ndjenjat e saj, këtë vit do të jetë në të njëjtën shkollë me të. Asaj nuk i vjen aspak mirë që Tomi sillet sikur asgjë të mos ketë ndodhur. Dhe, përvec kësaj, ai i prezanton edhe të dashurën e tij, e cila ka vetëm një dëshirë: të bëhët mikesha më e mirë e Kiokos. Shpirtvrarë, Kiokoja përgatitet për një kthim në shkollë, duke u përpjekur të hedhë gjithçka mbrapa krahëve. ', 4.50, '2024-05-30T16:24:00.024Z', 'Paper', 58, 17);
