INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355636, './Images/366.jpg', 'Globalizimi Dhe Vetdija Kulturore ', '2024-05-30T16:24:00.024Z', 408, 'Ky libër përmbledh më shumë se 20 artikuj, ligjërata dhe biseda të prof. Fei Xiaotong, nga fundi i viteve 1980 deri në fillim të viteve 2000. Tema e tyre qendrore është se si qytetërimet do të bashkëjetonin në kontekstin e globalizimit të shpejtë. ', 10.80, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355337, './Images/367.jpg', ' Mistere Vellimi 2', '2024-05-30T16:24:00.024Z', 384, ' Mistere , është vazhdimi I fuqishëm dhe iluminues i “Okultit” , duke thelluar hetimin përmes eksperiencave të jashtëzakonshme e të drejtpërdrejta të Wilsonit , në brendësi të paranormales , të okultit dhe të mbinatyrshmes .', 11.95, '2024-05-30T16:24:00.024Z', 'Paper', 54, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123400, './Images/78.jpg', 'Jeta Ime Me Stivenin ', '2024-05-30T16:24:00.024Z', 432, 'Ligji i gravitetit bart dhe ruan harmoninë e udhës së trupave qiellorë nëpër pafundësi, ndërkohë që dashuria bart dhe ruan harmoninë në jetën e gjeniut Stivën Hoking, teksa ai endet mes tyre pambarimisht. Rrugëtim mes yjesh, shkencë, kërkime, zbulime, art dhe emocione pa fund, që përçohen prej dëshmisë së gruas që u magjeps nga shkëlqimi i këtij ylli vezullues, fati i të cilit ishte aq i pasigurt, duke u bërë pjesë e rrugëtimit të tij përmes ëndrrave, sfidave, luftërave, rritjes dhe lavdisë, që duket se nuk kanë fund. ', 11.95, '2024-05-30T16:24:00.024Z', 'Paper', 13, 19);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123320, './Images/4.jpg', 'Anektoda Burimore', '2024-05-30T16:24:00.024Z', 44, 'Përmbledhja e anekdotave origjinale të Nasradin Hoxhës, njërit prej personaliteteve të rralla historike që zë një vend të rëndësishëm në kulturën dhe folklorin turk dhe jo vetëm turk. Vlera e Nasradin Hoxhës nuk matet me përjetimet e tij, por me mprehtësinë e mendimit, kritikën dhe thumbimin me të cilin ai vetë i rrëfente anekdotat, apo që populli ia atribuon se i ka rrëfyer ai. Rrëfimet e tij tallëse dhe satirike për shumë shekuj janë rrëfyer dhe rrëfehen edhe sot nga turqit kudo që jetojnë. Prandaj, ky libër sjell Nasradinin, anekdotat dhe të bëmat e tij në trajtën origjinale. ', 3.50, '2024-05-30T16:24:00.024Z', 'Paper', 33, 89);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123428, './Images/121.jpg', 'Evropa Monetare ', '2024-05-30T16:24:00.024Z', 224, 'Ky libër përmbledh: Një sintezë leksionesh të strukturuara, pjesë nga libra të autorëve të mëdhenj klasikë, ngjarjet më të rëndësishme ekonomike dhe sociale, si dhe shumë shembuj që ilustrojnë dhe konkretizojnë analizën ekonomike. ', 9.00, '2024-05-30T16:24:00.024Z', 'Paper', 57, 17);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123500, './Images/191.jpg', 'Edukimi I Stoikut ', '2024-05-30T16:24:00.024Z', 102, 'Ëndrra, kur jetohet me tepri ose bëhet familjare, shndërrohet në një realitet të ri; tiranizon si ai; resht së qeni strehë. Ushtritë e ëndërruara përfundojnë të mundura, si ato që bien dhe rrënohen në përplasjet dhe në betejat e botës. Nuk kam ndier kurrë nostalgji, se nuk kam pasur kurrë arsye për t’u ndier nostalgjik. Në ndjeshmërinë time kam qenë gjithnjë racional. Meqë me jetën time nuk bëra dot gjë, asgjë nuk më përmallon; meqë ajo që nuk ekziston, mund të jetë gjithçka, fare mirë mund të kisha ushqyer shpresa; sot as shpresa nuk kam, se nuk shoh kurrfarë arsyeje pse e ardhmja duhet të jetë e ndryshme nga e shkuara. ', 6.00, '2024-05-30T16:24:00.024Z', 'Paper', 4, 6);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123520, './Images/215.jpg', 'Antologji E Poeteve Te Pejes ', '2024-05-30T16:24:00.024Z', 288, 'Peja ka qenë dhe është edhe sot fidanishte e aktorëve të lavdishëm, të piktorëve të mëdhenjë dhe të poetëve të njohur, prandaj kjo antologji do të jetë jo vetëm dëshmi por edhe një hallkë e madhe e artë në zingjirin e gjatë të traditës poetike të qytetit. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 61, 11);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123356, './Images/43.jpg', 'Monument Vëllimi I', '2024-05-30T16:24:00.024Z', 251, 'Një vepër monumentale në shqip dhe anglisht ”Monumenti” që flet dhe ilustron trashëgiminë kulturore dhe historinë. Lexohet edhe si një roman, sepse është shkruar nga një mjeshtër i rrëfimit; sepse ka shumë personazhe, legjenda, histori të gjalla. ', 20.00, '2024-05-30T16:24:00.024Z', 'Paper', 38, 15);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123454, './Images/133.png', 'Me Quajne Carli Caplin ', '2024-05-30T16:24:00.024Z', 64, 'Çarli Çaplin është një nga figurat kryesore të historisë së kinemasë. Ishte biri i një çifti aktorësh jo të njohur. Brenda pak vitesh arriti të kapërcente mjerimin në të cilin jetonte, duke u bërë aktori më i famshëm dhe më i paguar i epokës së tij. Në dyzet vjet mori pjesë në më shumë se tetëdhjetë filma, ku ia doli të kryente me sukses edhe aktorin, regjisorin, kompozitorin, si dhe producentin. U konsiderua si një gjeni i kinemasë, fillimisht asaj pa zë, dhe më vonë i kinemasë me zë. Karriera e tij filloi me komeditë, pastaj me filmat sentimentalë dhe dramatikë, për të përfunduar me filmat që denonconin shoqërinë dhe politikën. Krijimi i tij më i madh ishte personazhi i Sharlotit, një vagabond unik i llojit të tij për shkak të mustaqeve, kapelës, shkopit… dhe peripecive të tij, të cilat kanë emocionuar breza të tërë. ', 3.50, '2024-05-30T16:24:00.024Z', 'Paper', 13, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123462, './Images/143.jpg', 'Enciklopedia E Perseve ', '2024-05-30T16:24:00.024Z', 160, 'A e dini pse? Një enciklopedi e rrallë, e ndërtuar në mënyrën më të përshtatshme për fëmijët: duke iu përgjigjur pyetjeve të tyre të shumta. Nëpërmjet fotove të përshkruara hollësisht, si dhe kurioziteteve nga fusha të ndryshme të dijes, ajo ngre pyetje dhe jep përgjigje, duke e bërë mënyrën e të mësuarit më të thjeshtë dhe ndërvepruese. Të gjitha pyetjet që fëmijët ngrenë mbi shkencën, gjeografinë, historinë, njerëzit dhe natyrën, do të marrin përgjigje në këtë libër të këndshëm dhe angazhues. Fotografitë trepërmasore do t’i ndihmojnë ata t’i kuptojnë plotësisht përgjigjet që u jepen pyetjeve të tyre. Ata mund të shohin nga brenda një vullkan, një lule apo edhe trupin e gjarprit, gjë që e bën vërtet të prekshëm informacionin interesant që marrin. A e dini pse kjo enciklopedi është ajo që duhet? Sepse di të japë përgjigje! ', 11.95, '2024-05-30T16:24:00.024Z', 'Paper', 28, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123358, './Images/35.jpg', 'Monument Vëllimi 3', '2024-05-30T16:24:00.024Z', 288, 'Një vepër monumentale në shqip dhe anglisht ”Monumenti” që flet dhe ilustron trashëgiminë kulturore dhe historinë. Lexohet edhe si një roman, sepse është shkruar nga një mjeshtër i rrëfimit; sepse ka shumë personazhe, legjenda, histori të gjall ', 20.00, '2024-05-30T16:24:00.024Z', 'Paper', 38, 14);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123429, './Images/122.jpg', 'Financat Personale ', '2024-05-30T16:24:00.024Z', 574, 'Mjetet, teknikat dhe ekuacionet harrohen lehtë, ndërsa logjika dhe parimet themelore që nxitin përdorimin e tyre, pasi të kuptohen, do të mbeten dhe do të bëhen pjesë e "personalitetit financiar" të cdo studenti. ', 25.00, '2024-05-30T16:24:00.024Z', 'Paper', 58, 16);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123430, './Images/123.jpg', 'Financat Publike ', '2024-05-30T16:24:00.024Z', 641, 'Ky libër është hartuar për te u përdorur në nivelet universitare, si dhe në programet pasuniversitare në degën e administrimit publik. Lexuesit duhet të kenë njohuri të teorisë mikroekonomike në nivelin e një kursi fillestar. ', 16.95, '2024-05-30T16:24:00.024Z', 'Paper', 51, 86);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355523, './Images/330.jpg', 'Nje Nate Magjike ', '2024-05-30T16:24:00.024Z', 286, ' Romani fokusohet në një ngjarje të përvitshme që zhvillohet në Paris, një darkë ekskluzive e quajtur "White Dinner" që mbahet në një vend misterioz çdo vit, ku pjesëmarrësit veshin rroba të bardha dhe krijojnë një ambient të magjishëm nën qiellin e hapur. Në këtë ngjarje, jetët e katër çifteve të ndryshme kryqëzohen dhe ndryshojnë përgjithmonë.', 14.00, '2024-05-30T16:24:00.024Z', 'Paper', 9, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355624, './Images/328.jpg', 'Kopshti I Luleve Sekrete ', '2024-05-30T16:24:00.024Z', 360, ': Londër, panairi i luleve në Çelsi, më i madhi në botë. Nën harqet e ngarkuara me trëndafila, Iris Donati është e lumtur, mes bimëve ndihet si në shtëpinë e saj. Një shtëpi e vërtetë, atë që kurrë s’e pati, sepse që në vogëli është endur nëpër botë vetëm me të atin. Ndërsa përkulet për të parë më mirë një kompozim lulesh, Irisi ngrin në vend: përballë, dy sy që ngjajnë si dy pika uji me të sajtë. Po ata flokë gështenjë. Vajza që ka përpara është identike me të quhet Viola. Edhe ajo i do fort lulet dhe buqetat e saj janë ndër më të kërkuarat në Londër. ', 9.95, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355539, './Images/369.jpg', 'Origjinat E Pushtetit Prones Pasurise ', '2024-05-30T16:24:00.024Z', 758, 'Shkencat janë të veçanta, por shoqëria shqiptare është një e tërë, dhe kemi nevojë ta kuptojmë atë njëherazi ekonomikisht, politikisht, filozofikisht, sociologjikisht, ndryshe vështrimi izolohet në ndarje hermetike! Profesori dhe Akademiku Artan Fuga vjen përpara lexuesit me një botim të riparë me titull “Origjinat e Pushtetit, Pronës, Pasurisë (në Shqipërinë pas-totalitare)” hedhur së fundmi në treg nga shtëpia botuese Papirus. Botimi vjen me një pako prej dy vëllimesh, “Shoqëria Periferike” dhe “Shoqëria Piramidale” ', 13.20, '2024-05-30T16:24:00.024Z', 'Paper', 57, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355640, './Images/370.jpg', 'Feminizmi Per Te Gjithe ', '2024-05-30T16:24:00.024Z', 170, ' Kritikja e njohur kulturore, Bell Hooks, ofron një vizion mikpritës dhe me zemër të hapur mbi gjininë, seksualitetin dhe shoqërinë, në një volum frymëzues dhe të hapur për publikun. Nëpërmjet një stili angazhues dhe provokues, Bell Hooks na paraqet një teori popullore të feminizmit të bazuar në sensin praktik dhe mençurinë e përvojës. ', 6.10, '2024-05-30T16:24:00.024Z', 'Paper', 65, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123535, './Images/233.jpg', 'Bukureshti I Shqiptareve ', '2024-05-30T16:24:00.024Z', 169, ' Do të gjeni dashurinë e mallin ndër shqiptarët e emigruar në Rumani që nga shekulli XVI, e më vonë të koncentruar në Bukuresht, ku organizoheshin e vepronin në forma e mënyra të ndryshme për ta ruajtur e kultivuar identitetin kombëtar, gjuhën, kulturën, traditat dhe për të ndihmuar atdheut e kauzës kombëtare shqiptare.', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 53, 9);
