INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123383, './Images/62.jpg', 'Brum ', '2024-05-30T16:24:00.024Z', 308, '"BRUM" është një libër që ndërthur gjuhën letrare, zhargonin dhe gjuhën e nënës. Ai përmban dizajn minimal dhe tregime të shkurtëra të shoqëruara me ilustrime. Ndahet në gjashtë kapituj ku secili prej tyre ka formën e vetë gjeometrike, temën dhe ngjyrën karakteristike në të cilën është projektuar. Protagonist i librit është vetë autori edhe brumi nga i cili u gatua në një periudhë tetëvjeçare. ', 7.50, '2024-05-30T16:24:00.024Z', 'Paper', 33, 8);
