INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123395, './Images/72.jpg', 'Xeh ', '2024-05-30T16:24:00.024Z', 401, 'Libri i tretë i Alban Fejzës, botuar në vitin 2020. Xehet, që qëndrojnë si kuptim prapa emrit të librit "XEH", janë pasuri të çmuara nëntokësore. Nxjerrja e tyre është e kushtueshme, por e merituar për vlerën. Ky libër shërben si një minierë e përpunuar e vlerave të përbashkëta të shoqërisë shqiptare. Përmes krijimtarisë, vepra bashkon poezinë, dizajnin grafik, ilustrimin, arkitekturën, muzikën, filmin, fotografinë, tipografinë, shkollën dhe artin e rrugës. ', 21.95, '2024-05-30T16:24:00.024Z', 'Paper', 32, 18);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123415, './Images/94.jpg', 'Lufta Ime  ', '2024-05-30T16:24:00.024Z', 304, ' Mos e merrni këtë libër si propagandë të nazizmit. Kjo është historia. Historia jonë dhe juaja, historia botërore. Ky libër është i ndaluar me ligj që të përdoret për te u propaganduar dhe për te u ndjekur si program për aktivitet politik dhe në këndvështrimin etiko – moral. Prandaj, mendojmë se duhet lexuar me një sy kritik për të nxjerrë mësimet e duhura që na i ka dhënë vetë gjyqi i historisë.', 17.85, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123000, './Images/304.jpg', ' Sibilat Ne Shkallet Tona ', '2024-05-30T16:24:00.024Z', 228, 'Në një dialog të mprehtë me Europën posthegjemoniste, poeti Rudolf Marku mbetet dishepull i paepur i një shkolle të përveçme, banor malesh dhe anëdetesh, poezia e tij i shmang perspektivat tokësore për të bërë bashk najadat dhe ujqërit. Marku është poet tragjik, por tragjedia nuk është fjala e tij e fundit. Ashtu si Odiseja homerik, ai e vetëzbulon trajektoren e vet si një barrë qëllimi që ia bën udhëtimin shumë më tepër sesa personal. Duke i rënë gjatë navigimit të historisë së kombit të vet, ai i çliron endjet e tij meditative prej përkitjes hapësinore dhe kohore, i familjarizuar me majëmalet e poezisë, ashtu siç parathotë Herakliti, kurrë nuk ia del të mbërrijë në shtëpi, ngase nuk mund të hysh dy herë në të njëjtin qytet. Destinacionet e tij mbeten trishtimi dhe vetmia. ', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 3, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123553, './Images/247.png', 'Nga Ana E Suanit ', '2024-05-30T16:24:00.024Z', 238, 'Në "Nga Ana E Suanit", Proust prezanton një pasqyrë të thellë dhe të ndjeshme të jetës shoqërore dhe emocionale të një grupi personazhesh aristokratikë dhe borgjezë të Parisit dhe provincave të Francës në fund të shekullit të 19-të dhe fillim të shekullit të 20-të. Në qendër të rrëfimit është fëmijëria dhe adoleshenca e protagonistit dhe narratori anonim, shpesh i konsideruar si alter ego i vetë Proust. ', 12.00, '2024-05-30T16:24:00.024Z', 'Paper', 66, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123532, './Images/227.jpg', 'Pyetje Te Tjera Ne Albanologji ', '2024-05-30T16:24:00.024Z', 383, ' Siç mund të vërehet dhe nga titulli, "Pyetje të tjera në albanologji – autorë dhe teza" është vijim i një projekti të hershëm shkencor të autorit për rindërtimin prapavajtës të traditës dhe historisë së mendimit shqiptar në antropologji, në letërsi e gjuhësi, në qytetërim, kulturë e besim, duke synuar rikthimin në dritë të njohjes të ndihmesave të autorëve të rendit parësor e dytësor në këto fusha.', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 8, 10);
