INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123424, './Images/116.jpg', 'Bazat E Finances Uet ', '2024-05-30T16:24:00.024Z', 636, 'Financa është një disiplinë e gjerë. Megjithëse ajo ka përdorur teorinë ekonomike, parimet e kontabilitetit dhe pasqyrat financiare, financa ka zhvilluar gjithashtu trupin e saj material. Ky i fundit bëhet objekt i tekstit Bazat e financës: hyrje në institucionet financiare, investime dhe drejtim. ', 14.95, '2024-05-30T16:24:00.024Z', 'Paper', 51, 13);
