INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123393, './Images/70.jpg', 'Tirana Horizontale ', '2024-05-30T16:24:00.024Z', 387, 'Go west është një idiomë e anglishtes që nënkupton vdekjen, vetëvrasjen. Kjo shprehje ironike është keqkuptuar nga ne shqiptarët, por edhe nga popujt e tjerë, duke e kthyer Europën, dhe Perëndimin në përgjithësi, në një iluzion paradiziak, në një shpëtim të mbramë. Ky dokumentar statik trajton marrëdhëniet midis etikës dhe estetikës, rreket të nxjerrë në pah dramat bashkëkohore që, ndër të tjera, nxisin edhe devijime kulturore. Këto 24 imazhe, 24 plane fikse, të vendosura si në vijën e frontit të lirisë së shprehjes, përbëjnë një sekondë kinema; ato zbehin hendekun midis foto-lajmit dhe përdorimit të fotografisë së çastit, si një medium i shprehjes dhe kreativitetit artistik në epokën e medieve të reja. ',7.00, '2024-05-30T16:24:00.024Z', 'Paper', 7, 19);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123394, './Images/71.jpg', 'Vidhni Si Artist ', '2024-05-30T16:24:00.024Z', 299, 'Në këtë libër rrëfehen 10 gjëra që askush nuk ua tregon për të qenë kreativ. Ky libër është aq i vlefshëm saqë brenda një kohe të shkurtër është shndërruar në një “bestseller” në Amerikë. Themeluesi i konferencës TED, Chris Anderson e vlerëson me një fjalë këtë libër: “I mrekullueshëm”. Në këtë kohë kreativiteti është arma më e fortë për më shumë sukses... Një guidë frymëzuese drejt kreativitetit në epokën dixhitale, “Vidhni si artist” paraqet dhjetë parime transformuese të cilat do t’i ndihmojnë lexuesit të zbulojnë anën e tyre artistike dhe të ndërtojnë një jetë më kreative. Asgjë nuk është origjinale, kështu që përqafoni ndikimet, edukohuni përmes punës së të tjerëve, bëni remix dhe ripërfytyroni që të zbuloni rrugën tuaj. Ndaj, shkruani librin që doni të lexoni, xhironi filmin që doni të shihni. Dhe së fundmi, qëndroni inteligjentë, qëndroni larg borxheve dhe rrezikoni të mërziteni në jetën e përditshme, që të jeni aventurierë dhe guximtarë në imagjinatën dhe punën tuaj. ', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 14, 16);
