INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355467, './Images/337.jpg', 'Atlasi I Flores Se Shqiperise ', '2024-05-30T16:24:00.024Z', 950, 'Botimi i volumit të parë të “Atlasit të Florës së Shqipërisë” përfaqëson librin e parë të këtij lloji, që pason atë të veprës katërvolumshe të “Florës së Shqipërisë”, të “Dendroflorës së Shqipërisë”, të disa florave rajonale, të “Udhëheqësit Fushor të Florës së Shqipërisë” dhe së fundi të “Excursion Flora ofAlbania”. Kjo vepër përbën një domosdoshmëri në studimet e thelluara në “Florën e Shqipërisë” dhe që do të kontribuojë në studimet e tjera në fushën e pyjeve, kullotave, bimëve aromatike e mjekësore, farmaceutikës, leksikografisë etj. ', 30.00, '2024-05-30T16:24:00.024Z', 'Paper', 44, 10);
