INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123340, './Images/16.jpg', 'Kanë Thënë Për', '2024-05-30T16:24:00.024Z', 779, 'Aforizmat , Proverbat dhe fjalët e urta popullore janë elemente të mençurisë , të qytetrimit dhe të kulturës njerëzore. Ato e shprehin filozofinë e jetës , e pasurojnë dhe e fisnikërojnë vetëdijen e njeriut për vetveten , e pasurojnë kulturën gjuhësore me shprehje të kuptueshme të qarta dhe e forcojnë lidhshmërinë e njeriut me njeri . ', 15.00, '2024-05-30T16:24:00.024Z', 'Paper', 38, 45);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123590, './Images/294.jpg', 'Moment Muzikor Proze Nga Izraeli ', '2024-05-30T16:24:00.024Z', 270, 'Përshkrimi: Në vëllim janë paraqitur disa nga arritjet më të sukseshme të prozës izraelite të viteve ‘70-‘90. Lexuesi do të gjejë në këto 14 tregime e novela, tema e krijime artistike të mrekullueshme. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123591, './Images/295.jpg', 'Aktori Dhe Zeri ', '2024-05-30T16:24:00.024Z', 155, ' Libër me përmbledhje profesionale nga lënda studimore dhe shkencore e të të kënduarit dhe shtjellimit të elementeve të vokalit në funksion të aktrimit skenik. Për larminë studimore stilistike të teknikës së zërit për aktorë, ky libër e kryen më së miri detyrën e përfaqësimit të domosdoshëm në programet e institucioneve akademike për aktrim. Ky libër evidenton dhe shtjellon qartë nocionet kryesore të terminologjisë e fizionomisë së aparatit të zërit dhe teknikave vokale. ', 2.95, '2024-05-30T16:24:00.024Z', 'Paper', 38, 18);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355463, './Images/359.jpg', 'Pergjigje Te Shkurtra Per Pyetjet Madhore ', '2024-05-30T16:24:00.024Z', 401, 'Kozmologu me famë botërore na jep mendimet e tij të fundit mbi pyetjet më të mëdha për universin, në këtë vepër të shkëlqyer të publikuar pas vdekjes së tij. Si u krijua universi? A do të mbijetojë njerëzimi në planetin tokë? A ekziston jetë inteligjente përtej sistemit tonë diellor? A mund të na e kalojë ndonjëherë në zgjuarsi inteligjenca artificiale?  ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 56, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123507, './Images/202.jpg', 'Vetja Dhe Tjetri ', '2024-05-30T16:24:00.024Z', 255, ' Në këtë vepër, Descombes risjell në vëmendje temat dhe autorët kryesorë të filozofisë së shek. XX (që nga Sartre e Merleau-Ponty, deri te Althusser, Deleuze, Foucault, Lyotard e të tjerë), duke risjellë një pjesë të kontekstit historiko-politik të periudhës që mbulon, si dhe duke zhvilluar një "diskutim filozofik" mbi këto tema dhe autorë.', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 42, 10);
