INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123335, './Images/11.jpg', 'Bubrreci Deputet', '2024-05-30T16:24:00.024Z', 255, 'Libri me tregime humoristike – satirike “ BUBRRECI DEBUTET” stigmatizon e dukuri negative të mjedisit tonë të mbushur plot me skena e veprime qesharake dhe tragji komike njëkohësisht. Si të tilla tregimet e këtij libri përkojnë fortë me arealin jetësor të mjedisit shoqëror. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 36, 19);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123426, './Images/118.jpg', ' Bota Pa Kufij', '2024-05-30T16:24:00.024Z', 308, 'Libri "Bota pa kufij" i Kenichi Ohmae është një libër që do të ndryshojë mënyrën se si e shohin menaxherët botën dhe biznesin e tyre, se si ato investojnë, komercializojnë dhe konkurrojnë. ',8.60, '2024-05-30T16:24:00.024Z', 'Paper', 52, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355313, './Images/319.jpg', ' Psikologjia Sociale', '2024-05-30T16:24:00.024Z', 388, 'Shpjegimi i sjelljeve të individit dhe shoqërisë vazhdon të tërheqë vëmendjen e psikologëve të shquar të të gjitha kohërave. Por edhe psikologët shqiptarë, duke përfaqësuar edhe në këtë fushë kulturën dhe nivelin e zhvillimit shqiptar në përgjithësi, kanë nisur të kontribuojnë gjerësisht në fondin e përgjithshëm të shkencës social-psikologjike. Ky libër është një përpjekje për të plotësuar nevojën për informacion aktual e të kompletuar social-psikologjik dhe për një kontribut shqiptar në këtë fushë. Sidoqoftë fjala e fundit thuhet në fund, pasi lexuesi ta lexojë librin… ', 14.95, '2024-05-30T16:24:00.024Z', 'Paper', 56, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355414, './Images/320.jpg', 'Frojdi Dhe Psikanaliza ', '2024-05-30T16:24:00.024Z', 316, ' Nuk ka neutralë në luftën e Frojdit. Admirimi dhe lajkat në njërën anë; skepticizmi dhe përçmimi në anën tjetër. Nuk ka asnjë hiperbolë. Një psikoanalist që aktualisht po mundohet ta mbajë Frojdin në panteonin e heronjve kulturorë mund të kënaqet vetëm me kritika të pamëshirshme kundër atyre që e kalojnë jetën, duke e shfaqur Frojdin si një sharlatan. Por, ka një pikë, në të cilën të dyja palët bien dakord: Qoftë mirë apo i sëmurë, Sigmund Frojdi, më shumë se çdo eksplorues tjetër i psiqikës njerëzore, ka hartuar mendjen e shekullit XX. Vetë egërsia dhe qëndrueshmëria e diktatorëve të tij ishte një atribut i dhunshëm i faktit që idetë e Frojdit vazhdojnë të jenë në fuqi.', 9.00, '2024-05-30T16:24:00.024Z', 'Paper', 30, 14);
