INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123433, './Images/126.jpg', ' Investimet 2', '2024-05-30T16:24:00.024Z', 401, 'Investimet (financiare) përdoret kryesisht për lëndët e analizës së investimeve financiare për studentët e nivelit BA, masterit dhe doktoraturës dhe për të gjithë ata që aspirojnë të bëhen Analistë Financiarë të Certifikuar. Përmes tij, studentët dhe profesionistët e financës njihen me çështjet kryesore me të cilat përballen aktualisht të gjithë investuesit, si edhe pajisen me aftësitë e nevojshme për kryerjen e një vlerësimi cilësor e sasior, jo vetëm të investimeve, por edhe të çështjeve financiare. Materiali mbulon gjerësisht dhe thellësisht modelet e rëndësishme financiare të vlerësimit të çmimit të kapitalit e të letrave të tjera me vlerë, si edhe hipotezat e konceptet e tjera të kërkimit financiar modern, të cilat jo vetëm janë subjekte të kërkimit shkencor, që japin kënaqësi intelektuale, por janë edhe tejet të rëndësishme nga pikëpamja praktike për investuesin e aftë. ', 18.00, '2024-05-30T16:24:00.024Z', 'Paper', 56, 13);
