INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123402, './Images/80.jpg', 'Enver Hoxha ', '2024-05-30T16:24:00.024Z', 398, 'Enver Hoxha është biografia më e suksesshme e shkruar deri më tani, e cila i ka kaluar kufijtë e Shqipërisë, duke u përkthyer edhe në anglisht. Gazetari i njohur, Blendi Fevziu sjell një profil ndryshe të diktatorit, duke përdorur kryesisht arkivat sekrete të Partisë së Punës, që janë hapur kohët e fundit, si arkivin personal të famljes Hoxha, dhe dëshmi të protagonistëve. Ndonëse autori ka këndvështrimin e tij për këtë figurë misterioze, sërish ngelet një prej botimeve që nuk anashkalohet kurrësesi nëse do të kuptosh Shqipërinë komuniste gjysmëshekullore dhe tiranin e saj. ', 15.00, '2024-05-30T16:24:00.024Z', 'Paper', 51, 33);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123403, './Images/81.jpg', 'Ditari I Che Guevares ', '2024-05-30T16:24:00.024Z', 288, 'Ditët më dramatike të jetës në shënimet personale të Çe Guevares. Takimi sekret I ÇE-së me Mehmet Shehun në Tiranë dhe bisedimet me delegacionin shqiptar në Kubë . ', 8.60, '2024-05-30T16:24:00.024Z', 'Paper', 21, 78);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123378, './Images/55.jpg', 'Historia E Arkitektures Moderne 1', '2024-05-30T16:24:00.024Z', 306, 'Në vëllimin e parë të Historisë së arkitekturës moderne janë dokumentuar origjina e lëvizjes moderne (nga Revolucioni francez e këndej), periudha e parë pioniere me Arts and Crafts të Morris dhe Art Nouveau të arkitekteve Horta, van der Velde, Olbrich, Gaudi dhe Loos, periudha racionaliste e shënjuar nga Le Corbusier, Gropius dhe Bauhaus, Mies van der Rohe dhe nga ekspresionizmi i Mendelsohnit dhe, në fund stina organike, protagonist i së cilës është arkitekti i shquar, Alvar Aalto', 26.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 14);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123379, './Images/56.jpg', 'Hyrje Ne Arkitekture', '2024-05-30T16:24:00.024Z', 401, 'Hyrje në arkitekturë, vepër tashmë klasike plotësisht e përditësuar në sistemin e vet ikonografik mbetet një domosdoshmëri për këdo që dëshiron një njohje fillestare të arkitekturës së shkuar dhe të tashme. Është paksa më e zgjeruar se “historia e arkitekturës” që mësohet në shkollat e mesme italiane, e cila lidh me të njëjtin fill artin klasik dhe artin italian dhe që vetëm së fundmi ka marrë në shqyrtim Europën dhe botën, për periudhën “moderne”. Ky libër synon të nxisë një diskutim të gjerë, duke folur pikërisht për shprehitë e marra nga edukimi bazë dhe duke hedhur së paku ndonjë dyshim mbi ndarjen vertikale të “lëndëve”; ashtu si në shkollë diskutimi mbështetet mbi përvojën që studentët eksperimentojnë në tavolinën e vizatimit, po ashtu, duke iu drejtuar një publiku më të gjerë, diskutimi duhet mbështetur mbi përvojën e qytetit që çdokush e zotëron, për të treguar se problemet e arkitekturës nuk janë çështje specialistësh, por prekin interesat e gjithsecilit." ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123533, './Images/232.jpg', 'Kush Jemi Ne Shqipetaret ', '2024-05-30T16:24:00.024Z', 374, 'Në këtë libër, studiuesi dhe shkrimtari, Ndriçim Kulla, ka përmbledh tiparin e shqiptarëve kështu: "Ne jemi një popull i kontrasteve. Kontrastet ekzistojnë dhe brenda karakterit tonë individual. Ne jemi të rezervuar, por individualistë, bujarë, por vuajmë nga kotësia, mikpritës, por edhe hakmarrës, e mbajmë fjalën, por jemi edhe dyshues, mund të shkojmë deri në fund të botës, por nuk duam ta dimë çfarë bëhet pas gardhit të shtëpisë sonë, jemi trima, por në betejat që i zgjedh vetë secili nga ne"...', 9.80, '2024-05-30T16:24:00.024Z', 'Paper', 45, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123534, './Images/234.jpg', ' Kujtime Nga Shqiperia E Eperme', '2024-05-30T16:24:00.024Z', 401, 'ibri ofron një pasqyrë të detajuar të kulturës, traditave, dhe jetës së përditshme të shqiptarëve të asaj kohe, duke përfshirë edhe përshkrime të peizazheve dhe marrëdhënieve shoqërore. Përmes këtij vepre, Degrand sjell një dëshmi të rëndësishme historike dhe antropologjike për rajonin dhe popullsinë shqiptare. ', 8.80 , '2024-05-30T16:24:00.024Z', 'Paper', 45, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355519, './Images/325.jpg', ' Ndryshime', '2024-05-30T16:24:00.024Z', 408, 'Qëndruan të trishtuar pa ia ndarë sytë njeri-tjetrit për një kohë të gjatë. Pastaj, pa thënë as edhe një fjalë, ai u përkul tek ajo dhe pasi e mori fytyrën e saj me ëmbëlsi në pëllembet e veta, e puthi. Ajo i mbylli sytë dhe ndjeu se si buzët e saj shkriheshin në të puthurën e tij, kurse shpirti i dukej sikur po i fluturonte lart në qiell. Asaj iu morën mendtë, prandaj u ngjesh te Piteri, pastaj e ngriti kokën dhe vështroi nga ai gjithë pikëllim, preku me gishtërinj fytyrën e tij, ndërsa doktori i puthte majat e gishtave të saj ', 13.95, '2024-05-30T16:24:00.024Z', 'Paper', 9, 10);
