INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355341, './Images/371.jpg', 'Dija Universiteti Dhe Demokracia ', '2024-05-30T16:24:00.024Z', 350, 'Në ditët tona, debati mbi Universitetin dhe vetë idea e Universitetit janë shumë të ndryshme nga disa prej debateve më të mëdha mbi universitetin që janë zhvilluar në shekullin e 19-të dhe në gjysmën e parë të shekullit të 20-të, ose edhe pas Luftës së Dytë Botërore. Vizionet grandioze dhe programatike mbi universitetin modern, të parashtruara në disa vepra madhore nga mendimtarë të shquar si, Wilhelm von Humboldt, John Henry Newman, John Dewey, Karl Jaspers , Talcott Parsons Jürgen Habermas dhe Pierre Bourdieu, kanë reflektuar besimin tek universiteti si një institucion që ka një mision moral dhe kulturor iluminist në shoqëri. ', 12.00, '2024-05-30T16:24:00.024Z', 'Paper', 10, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123355, './Images/33.jpg', 'Lidhja Shqiptare E Prizrenit', '2024-05-30T16:24:00.024Z', 754, '“Lidhja Shqiptare e Prizrenit”, ishte një organizatë patriotike revolucionare e mbarë shqiptarëve pavarësisht nga përkatësia fetare, shoqërore dhe krahinore, e cila veproi në kushtet e sundimit shekullor osman dhe në vorbullën e Krizës Lindore të viteve 70 të shek. XIX, për të realizuar programin e Rilindjes Kombëtare Shqiptare: mbrojtjen e tërësisë tokësore të atdheut dhe krijimin e shtetit kombëtar shqiptar. ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 44, 13);
