INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123578, './Images/281.jpg', ' Ceshtja Karavaxho', '2024-05-30T16:24:00.024Z', 422, ' Gabriel Aloni, restaurues veprash arti dhe agjent sekret, ndodhet në Venecia, ku policia italiane i bën një kërkesë të pazakontë. Tregtari i veprave të artit, Xhulian Isherudi, është përballur me një skenë drithëruese krimi dhe po mbahet si i dyshuar. Viktima është një ish-agjent, i cili ruan një të fshehtë; ai është trafikant veprash arti në shërbim të një koleksionuesi misterioz. Për të shpëtuar mikun e tij, Gabrieli duhet të gjurmojë pikturën më të kërkuar në botë... ', 14.00, '2024-05-30T16:24:00.024Z', 'Paper', 64, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123579, './Images/282.jpg', 'Vajza Angleze', '2024-05-30T16:24:00.024Z', 424, '  Madalena Harti është një vajzë e bukur, e zgjuar dhe e etur për sukses, me një të ardhme të ndritur në qeverinë britanike. Ajo vjen nga një fëmijëri e varfër, por mbart edhe një të fshehtë të errët: është e dashura e kryeministrit Xhonatan Lankaster. Pasi Madalena zhduket në ishullin e Korsikës, mësohet se rrëmbyesit e saj janë në dijeni të këtij fakti dhe kanë ndër mend ta bëjnë kryeministrin britanik t’i paguajë shtrenjtë mëkatet e tij. Nga frika e një skandali që mund t’i shkatërrojë karrierën politike, Lankasteri vendos ta zgjidhë çështjen privatisht, pa përfshirë policinë. ', 14.00, '2024-05-30T16:24:00.024Z', 'Paper', 64, 18);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123469, './Images/148.jpg', ' Me Quajne Musine Kokalari', '2024-05-30T16:24:00.024Z', 64, 'Lindi në Adana të Turqisë dhe kaloi fëmijërinë në Gjirokastër, qytetin përrallor, që i dukej sikur fluturonte. Ajo është shkrimtarja e parë shqiptare, që u vlerësua që në fillim për artin e saj me nivel të lartë. Në shkrimet letrare apo ato publicistike ajo vuri në qendër gruan shqiptare tradicionale dhe shpalosi nevojën për emancipimin e vajzave. Ishte një intelektuale demokrate, antifashiste e antikomuniste e shpallur, që besonte te liria e mendimit dhe te liria e artit. Që gjatë Luftës themelon me intelektualë të tjerë Partinë Social-Demokrate. Mbas çlirimit, luftoi politikisht për mbrojtjen e lirive demokratike dhe të pluralizmit politik. Për këtë arsye, regjimi komunist e burgosi në moshën 27-vjeçare. ', 3.95, '2024-05-30T16:24:00.024Z', 'Paper', 13, 6);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123357, './Images/34.jpg', 'Monument Vëllimi 2', '2024-05-30T16:24:00.024Z', 195, 'Një vepër monumentale në shqip dhe anglisht ”Monumenti” që flet dhe ilustron trashëgiminë kulturore dhe historinë. Lexohet edhe si një roman, sepse është shkruar nga një mjeshtër i rrëfimit; sepse ka shumë personazhe, legjenda, histori të gjalla. ', 20.00, '2024-05-30T16:24:00.024Z', 'Paper', 38, 13);
