INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (1235546, './Images/316.jpg', 'Mbi Psikiken Njerezore ', '2024-05-30T16:24:00.024Z', 182, ' Mbi natyrën e psikikës", të përfshirë në këtë libër ai përshkruan në mënyrë mjeshtërore teorinë e vet të pavdekshme mbi pavetëdijen dhe lidhjet e saj me mendjen e vetëdijshme. Ndërsa në esenë tjetër të mrekullueshme "Mbi energjinë psikike", ai mbron dhe interpreton teorinë e libidos, një tjetër gur themeli në ndërtesën madhështore të veprave që ai i la psikologjisë dhe një faktor kyç që shërbeu për ndarjen e madhe nga Frojdi.', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355511, './Images/317.jpg', 'Burri Qe Pandehu Gruan Per Kapele ', '2024-05-30T16:24:00.024Z', 280, 'Ky libër është i rrallë sepse sjell përmes punës së një psikiatri dëshmitë e personave me dëmtime neurologjike, shumica e të cilëve shfaqin dhunti të gjenisë njerëzore në matematikë, art etj. The New York Times Review of Books thotë për këtë libër: Depërtues.... me kthjelltësinë dhe aftësinë e një rrëfyesi të talentuar ', 10.60, '2024-05-30T16:24:00.024Z', 'Paper', 38, 21);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123465, './Images/145.jpg', 'Une Di ', '2024-05-30T16:24:00.024Z', 305, 'Një libër për më kureshtarët? Për ata që nuk ngopen kurrë me informacion dhe që duan të mësojnë gjithçka për gjithçka? Ky është i duhuri! Në të do të mësoni për avionët më të shpejtë, për peshkaqenët më të frikshëm dhe për rekordet më të çuditshme. Galeria e madhe e fotove që do të gjeni në brendësi të librit, titujt dhe kërshëritë, faktet e pabesueshme e shumë të tjera, do të jenë një thesar që ka gjithmonë diçka për të treguar. Ky libër duket sikur thotë: Më pyet, dhe do të të përgjigjem. Po ashtu, do të të mësojë si të zbarkosh në një kometë, si të shëtisësh në hapësirë dhe si t’i zbulosh mrekullitë e botës sonë. Kështu, do të thuash pa frikë: Unë di! ', 24.00, '2024-05-30T16:24:00.024Z', 'Paper', 28, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355612, './Images/318.jpg', ' Psikologjia E Intelegjences', '2024-05-30T16:24:00.024Z', 156, 'Ne kete liber doctor Jung, I cili eshte autori i disa prej hipotezave me provokuese ne psikologjine modern, pershkruane ate qe ai e quan nje funksion te mirfillte fetar ne mendjen e pavetedijeshme. ', 6.90, '2024-05-30T16:24:00.024Z', 'Paper', 45, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123498, './Images/189.jpg', 'Baza E Moralit ', '2024-05-30T16:24:00.024Z', 345, 'Do të binim në një gabim të madh dhe shumë fëminor nëse bësonim se të gjitha veprimet e drëjta dhe të ligjshmë të njerëzimit kanë zanafillë morale. Përkundrazi, është e vërtetë se midis drëjtësisë së ushtruar nga njerzit dhe ndershmërisë së mirfiltë të zëmrës, ekziston një lidhje analoge si ajo midis shprehjes së mirësjëlljes dhë dashurisë së sinqërtë ndaj tjetrit. Vëtëm dashuria e sinqërtë është në gjëndje ta mundë egoizmin realisht, jo vëtëm në dukje, ashtu siç bën mirësjellja. ', 12.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 23);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123398, './Images/76.jpg', ' Ali Baba', '2024-05-30T16:24:00.024Z', 198, ' Sot, Jack Ma (MaYun) është një prej gjigantëve të botës së internetit. Kompania e tij, Alibaba, është bërë platforma më e madhe në botë për tregtinë online. Ngritja e Jack Ma-së dhe Alibaba-s ka qenë integralë për zhvillimin e ekonomisë së stërmadhe të internetit në Kinë. Ky libër paraqet përvojat jetësore të Jack Ma-së, karrierën e tij si sipërmarrës dhe të gjithë procesin e krijimit të Alibaba-s. Duke shkuar pas skenave, autori tregon se si Jack Ma rrëzoi traditat dhe e ktheu Alibaba-n në një organizatë të fuqishme, e cila theu rekordin botëror si oferta më e lartë për një kompani publike gjatë regjistrimit në bursën e New York-ut në vitin 2014.', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 49, 10);
