INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123417, './Images/96.jpg', 'Bijte E Nenes ', '2024-05-30T16:24:00.024Z', 278, 'Botime KOHA sjell përmbledhjen e veçantë kushtuar Shërbimit Shëndetësor të Shoqatës humanitare bamirëse “Nëna Tereze” Nën përkujdesjen dhe përcjellë me komentet e dr. Gani Demollit, lexoni se si lindi shërbimi më unikal shëndetësor, i cili mundësoi mbijetesën e popullatës civile kosovare në kohët e represionit më të ashpër dhe të luftës. Kuptoni, nëpërmjet raporteve të atyre pak mediumeve të shkruara të Kosovës të viteve ’90-a, se sa e vështirë ishte lufta e këtyre mantelbardhëve për t’i dhënë shpresë popullit. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 38, 19);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123418, './Images/97.jpg', 'Ditari I Nje Izolanti ', '2024-05-30T16:24:00.024Z', 360, 'Mos e përlyej murin me gjak - i kishte thënë mjeku kasap Kolë M. Berishës në Leskovc, teksa ua kishte bërë me dije milicëve se mund të vazhdonin ta rrihnin deri në alivanosje, sepse vlerësonte se ende kishte fuqi për ta përballuar rrahjen brutale. E kishte vlerësuar, bashkë me xhelatët e tjerë, se "krimi" për të cilin akuzohej: refuzimi që te i ndërrohej emri i shkollës së mesme "Luigj Gurakuqi" në Klinë, ku kishte qenë sekretar, meritonte ndëshkimin që do të shërbente si shembull se çfarë mund te i gjente të gjithë ata që ndanin mendimin me të. Është libër-dokument të cilin duhet ta lexojnë sidomos brezat e rinj, sepse paraqet një pjesë shumë të rëndësishme të historisë sonë që dëshmon se çlirimi dhe pavarësia e Kosovës ndodhën si rezultat i përpjekjeve, shpesh mbinjerëzore, të patriotëve që nuk luftuan për famë e pasuri, por për mbijetesën dhe mirëqenien e popullit të cilit i takojnë. ', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 38, 23);
