INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123427, './Images/120.jpg', 'Evropa Ekonomike Dhe E Ardhmja E Sa ', '2024-05-30T16:24:00.024Z', 286, ' Objektivi i këtij libri është të bëjë një analizë rigoroze të përmbajtjes dhe të natyrës së Evropës ekonomike dhe sociale që po ndërtohet aktualisht, me qëllim vënien në dukje të forcave dhe dobësive që mund të shpjegojnë krizën e besimit që ajo po kalon. Ai ka gjithashtu për qëllim të tregojë sfidat e ndryshme, raportet e forcave dhe skenarët që do të përcaktojnë Evropën e të nesërmes: midis zonës së shkëmbimit të lirë dhe hapësirës politike të integruar. Ky libër u drejtohet studentëve të shkencave ekonomike dhe sociale, si dhe të gjithë lexuesve të interesuar për këto fusha. ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 57, 14);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123501, './Images/192.jpg', 'Politika ', '2024-05-30T16:24:00.024Z', 216, ' Një nga veprat më të famshme të Aristotelit dhe një traktat i mrekullueshëm për qeverisjen dhe mënyrat e saj, për demokracinë dhe raportet e qytetarit me të dhe me shtetin, që i japin këtij vëllimi një vlerë jashtëzakonisht aktuale edhe sot. ', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 14);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123502, './Images/193.jpg', 'Shpirti I Gruas ', '2024-05-30T16:24:00.024Z', 448, ' Kleopatra, mbretëresha e pavdekshme e Egjiptit, "me prejardhje gjysëm maqedonase e (me sa duket) gjysëm greke, "duhet të vendoset gjithashtu mes mbrojtësve të një mbretërie. Shkrimtarët kanë folur shumë për "joshjen e saj seksuale", por historianët që kanë studiuar dokumentat e historisë së saj shkojnë përtej këtij gjykimi sipërfaqësor të personalitetit të saj dhe shohin rolin e saj si administratore dhe mbrojtëse e popullit. Ata na thonë se ajo nuk ishte "veçantërisht e bukur" dhe i joshte burrat e pushtetshëm me anë të aftësive të tjera.  ', 10.50, '2024-05-30T16:24:00.024Z', 'Paper', 45, 7);
