INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123580, './Images/283.jpg', 'Agjenti Anglez', '2024-05-30T16:24:00.024Z', 600, 'Një pjesëtare e rëndësishme e familjes mbretërore britanike vritet në një sulm me bombë. Shërbimet Sekrete Britanike i drejtohen të vetmit person, i cili mund ta gjejë vrasësin: agjentit legjendar, Gabriel Alon. Në shënjestrën e Gabrielit është Ejmon Kuini, mjeshtër në prodhimin e bombave dhe vrasës me pagesë. Përkrah, Aloni ka ish-komandon dhe vrasësin me pagesë, Kristofer Keler, i cili i njeh mirë aktivitetet e Kuinit. Por, Gabrieli nuk e ka kuptuar ende që ka të bejë me një armik të vjetër, i cili dëshiron vdekjen e tij, më shumë se gjithçka tjetër. Shumë shpejt, ai do të kuptojë se edhe "vdekja" ka anët e saj të mira... ', 14.00, '2024-05-30T16:24:00.024Z', 'Paper', 64, 18);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123581, './Images/284.jpg', 'Dhoma', '2024-05-30T16:24:00.024Z', 344, ' Për Xhekun pesë vjeçar, “Dhoma” është e gjithë bota. Aty ka lindur dhe është rritur; aty jeton së bashku me Manë e tij, aty mësojnë, lexojnë, hanë, flenë dhe luajnë. Natën, për siguri, Maja e mbyll në dollap, që të flerë sa herë vjen Plaku Nik. Një roman vërtet i jashtëzakonshëm. “Dhoma” na dhuron një mënyrë krejt të veçantë rreth të folurit për dashurinë, e njëkohësisht na jep një këndvështrim të gjallë të botës ku jetojmë. ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 14, 7);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355418, './Images/324.jpg', 'Ps Te Dua ', '2024-05-30T16:24:00.024Z', 460, 'Një histori e ëmbël dhe e hidhur njëkohësisht në një bestseller të padiskutueshëm në të gjithë botën. Një fëmijëri e kaluar së bashku dhe një ndarje e pamenduar kurrë më parë. Vdekja e Xherrit e shkretoi Olin derisa në prag të 30- vjetorit të saj, ai kthehet me një tufë pusullash, të cilat e udhëheqin Olin drejt një jete të re, ku në fund të secilës prej tyre shënon: "P.S. Të dua". Ndërsa pusullat hapen një e nga një dhe viti numëron ditët e fundit, Oli bëhet njëherësh inkurajuese dhe sfiduese. Njeriu që e njihte më mire se kushdo, i tregon asaj se jeta vazhdon. ', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 58, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355542, './Images/372.jpg', 'Etika Protestante ', '2024-05-30T16:24:00.024Z', 250, ' Njëra prej veprave më të mëdha të sociologut më të madh të shekullit XX, ku paraqitet një analizë e shkëlqyer, por dhe rigoroze e burimeve dhe mënyrave të zhvillimit të shoqërisë kapitaliste. Libri është i pajisur me një numër të madh shënimesh dhe ofron një nga përkthimet më të mira të kësaj fushe.', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355643, './Images/373.jpg', ' Teorite Sociologjike', '2024-05-30T16:24:00.024Z', 401, ' Në të gjitha periudhat historike jeta sociale e individëve bazohet kryesisht në raportet e pushtetit sipas binomit urdhër-bindje.Me formimin e shteteve moderne dhe si rrjedhim afirmimin e parimit të demokracisë, besohej që raportet urdhër-bindje dhe dhunat detyruese, tipike të shteteve autoritare, do të zhdukeshin. ', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123374, './Images/51.jpg', 'Forma Permbajtja Arkitekturat', '2024-05-30T16:24:00.024Z', 869, '“Forma, përmbajtja dhe Arkitektura – Udhëtim në banesën popullore shqiptare”, i autorëve Maksim Mitrojorgji dhe Joli Mitrojorgji është libri që sjell një vështrim interesant për rrugën që është ndjekur për realizimin e formës në arkitekturën e banesës popullore shqiptare. Ky botim mbi arkitekturën e banesës popullore shqiptare ndjek rrjedhën e një pune kolosale, mbi pesëdhjetë vjeçare, të bërë në Shqipëri, për evidentimin e monumenteve të arkitekturës në përgjithësi dhe atyre të tipologjisë së banesës popullore në veçanti.Në arkitekturën e traditës, jo domodshmërisht duhet të vlerësojmë vetëm atë që shohim si të ndërtuar, një relike me vlerë të trashëguar nga e shkuara. ', 37.00, '2024-05-30T16:24:00.024Z', 'Paper', 10, 8);
