INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123434, './Images/127.jpg', 'Kurthet E Papunesise ', '2024-05-30T16:24:00.024Z', 246, ' Libri “Kurthet e papunësisë” i studiuesit të njohur të çështjeve sociale Kosta Barjaba vjen në një moment kur në Shqipëri, por dhe në botë, diskutimet, analizat, propozimet dhe zgjidhjet për çështjen e punësimit dhe papunësisë janë në fokus më shumë se kurrë. Krahas shqetësimeve për menaxhimin e situatës së borxheve publike apo nxitjen e rritjes ekonomike, papunësia duket se përbën sfidën më të madhe për ekonomitë, qeveritë dhe shoqërinë e sotme. Si ta përballojmë atë? Nga shkaktohet më së shumti papunësia? A kemi gjetur sistemet më të mira të punësimit dhe reduktimit të papunësisë? Është treguesi i përkohshëm i lidhur me ciklin ekonomik dhe krizën globale apo një fenomen gjithnjë e më strukturor për një kategori ekonomish e vendesh të caktuara? A ka një formulë globale për luftën kundër papunësisë? A mund të kopjohen përvojat e të tjerëve në këtë aspekt? Po Shqipëria si duhet të përballet me këtë sfidë? ', 9.00, '2024-05-30T16:24:00.024Z', 'Paper', 51, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123472, './Images/152.jpg', 'Me Quajne Isak Njuton ', '2024-05-30T16:24:00.024Z', 64, 'Përshëndetje, më quajnë Njuton, Isak Njuton. Në moshën tuaj adhuroja të ndërtoja shpikjet e mia të çuditshme. Një ditë ngrita në fluturim disa balona, të cilave u ngjita disa drita që i frikësuan fqinjët. Sa gallatë! Më pëlqenin shumë edhe orët, kështu që arrita të ndërtoja një orë që funksiononte me forcën e ujit, dhe një ore die!lore, që më bëri të famshëm në gjithë vendin. Por nuk njihem nëgjithë botën për këtë orë, por per një kokërr mollë. Po, po, për një kokërr të thjeshtë molle! ', 3.95, '2024-05-30T16:24:00.024Z', 'Paper', 13, 17);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123435, './Images/129.jpg', ' Kontabiliteti I Kostos', '2024-05-30T16:24:00.024Z', 527, 'Kontabiliteti i Kostos jep informacion të detajuar të kostos i cili është i nevojshëm për menagjimin për të kontrolluar operacionet aktuale dhe klanin për të ardhmen. ', 35.00, '2024-05-30T16:24:00.024Z', 'Paper', 28, 7);
