INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123508, './Images/203.jpg', ' Jete Paralele', '2024-05-30T16:24:00.024Z', 444, '  Ky libër shënon një prej burimeve më të rëndësishme historike për personalitetet dhe epokën në të cilën jetoi Plutarku. Aftësia dhe mjeshtria e tij janë të njohura edhe sot botërisht në mënyrën sesi bëhet jetëshkrimi, duke u shndërruar në këtë mënyrë biografi i parë modern i shumë personaliteteve të shquara.', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 45, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123484, './Images/178.jpg', 'Koha Per Te Ikur ', '2024-05-30T16:24:00.024Z', 289, 'Ray Bradbury është një nga shkrimtarët amerikanë më të njohur në botë. Krijimtaria e tij përfshin romane, tregime, publicistikë, skenare filmash. Vepra e tij është shpërblyer me çmimet më të mëdha letrare, si, Çmimi O’Henry, Çmimi Linkoln etj. Kritikët dhe lexuesit bashkohen në mendimin se 451 Farenheit (Farenheit 451 °) - përkthyer edhe në shqip në vitet ’80 - është kryevepra e këtij shkrimtari tepër aktiv deri në ditët e sotme. Megjithatë, tregimet e shkurtra, mbeten pjesa më përfaqësuese në krijimtarinë e këtij autori. Tematika e këtyre tregimeve sillet që nga realizmi kritik e deri tek futurizmi i subjekteve të së ardhmes. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 43, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123509, './Images/204.jpg', 'Filozofia Moderne ', '2024-05-30T16:24:00.024Z', 664, 'Ekzistenca jonë nuk ka një themel të fundëm; ajo është një fakt brutal për të cilin ne nuk mund të gjejmë asnjë arsye, qëkur të gjitha arsyet janë krijuar brenda jetës dhe jo nga këndëvështrimi jashtë jetës, tek i cili ne kurrë nuk mund të arrijmë. Ankthi drejt vdekjes është ‘ontologjikë’ ; ai shpërhapet mbi fytyrën e ekzistencës në vetvete dhe shkatërron ‘bazën e qenies’. ', 17.60, '2024-05-30T16:24:00.024Z', 'Paper', 51, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123519, './Images/214.jpg', 'Fjalori Kazar ', '2024-05-30T16:24:00.024Z', 296, 'Një bestseller ndërkombëtar, Fjalori Kazar është cilësuar nga The New York Times Book Review si një nga librat më të mirë të vitit. I shkruar në dy versione, në gjininë mashkullore dhe në gjininë femërore, Fjalori është libri imagjinar i njohurive të Kazars, një popull që lulëzoi diku përtej Transilvanisë midis shekullit të shtatë dhe të nëntë. ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 3, 7);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123510, './Images/205.jpg', ' Filozofia E Aktualitetit', '2024-05-30T16:24:00.024Z', 204, ' Libri Filozofia e aktualitetit është shkruar në formë bisedash me lexuesin. Ato marrin shkas nga takime e ngjarje që e kanë nxitur autorin për ballafaqimin e ideve me realitetin e kohës, për refleksione të natyrës filozofike, që në epokën e sotme të globalizimit, kur filozofia merr një status të posaçëm, lipset të bëhen praktikë e zakonshme, sepse shërbejnë për të bashkuar në një subjektin njohës dhe subjektin veprues. ', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 63, 9);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123512, './Images/207.jpg', 'Gjuha E Verteta Dhe Logjika ', '2024-05-30T16:24:00.024Z', 193, 'kzistojnë dy lloj parashtrimesh të kuptimtë: parashtrimet analitike (si në matematikë), të cilët janë të vërtetë me përkufizim; dhe parashtrimet që mund të verifikohen nga përvoja. Por nëse është kështu, një pjesë e madhe e filozofisë tradicionale, përfshi etikën, estetikën dhe metafizikën, dhe gjithashtu teologjinë, lidhet me parashtrime që nuk kanë kuptim të drejtpërdrejtë. Ky përfundim i jashtëzakonshëm është në zemrën e këtij libri epokë – bërës, manifesti klasik i pozitivizmit logjik. Më 1936, ai ishte si një frymëmarrje e çliruar; mbi gjysëm shekulli më vonë, forca e tij tronditëse mbetet e njëjtë.   ', 7.80, '2024-05-30T16:24:00.024Z', 'Paper', 45, 5);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123496, './Images/187.jpg', 'Bota Si Vullnet Dhe Perfaqesim 12 ', '2024-05-30T16:24:00.024Z', 304, ' o bashkëkohësve të mi, jo bashkëqytetarëve të mi, por njerëzimit po ia dorëzoj veprën time.... me një rëndesë të brendshme dhe me besimin se herët a vonë do të arrijë tek ata, të cilëve mund t’u drejtohet. Por, nga ana tjetër, qetësisht i dorëzuar të shoh të prekë, në tërësinë e vet, fatin që i takon gjithmonë së vërtetës, në çdo fushë të dijes: fat, që thotë se të vërtetës i është caktuar veçse një triumf i shkurtër, midis dy hapësirave të gjata kohe, në të cilat ajo dënohet si paradoksale ose nënçmohet si banale.E vërteta nuk është një grua e përdalë, që u hidhet në qafë atyre që nuk e dëshirojnë; përkundrazi, ajo është një bukuroshe kaq shumë ngurruese, saqë edhe ai që arrin të sakrifikojë gjithçka nuk mund të jetë i sigurt t’i fitojë favoret e saj. Jeta është e shkurtër, por e vërteta punon me largpamësi e rron shumë gjatë. le ta themi pra të vërtetën!', 8.00, '2024-05-30T16:24:00.024Z', 'Paper', 45, 12);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123463, './Images/141.jpg', 'Spihunazhologjia ', '2024-05-30T16:24:00.024Z', 300, 'Jemi me mision! Më saktë, spiuni Spenser Blejk, i njohur ndryshe si Agjenti K është me mision dhe ai është treguar aq zemërgjerë, sa të mbajë shënime të mrekullueshme, tepër sekrete e t’i ndajë ato me ne! Çfarë udhëzuesi! Të bësh tënden mjeshtërinë e spiunazhit, të mësosh si mblidhet informacioni, të ulesh në punishten e falsifikatorit, si dhe të lexosh informacion tërësisht të klasifikuar! Është viti 1958 dhe agjenti britanik ka nisur një mision të fshehtë për të zbuluar një organizatë kriminale. Ndërsa përshkon botën nga Skocia në Berlin, Las Vegas e Kubë, ai regjistron teknikat e tij sekrete që mund t’i gjeni në këtë udhëzues për agjentë të rinj, domethënë, për ju! Shumë dokumente sekrete, një filtër deshifrues dhe zmadhues, kode të fshehta, foto, libërtha brenda librit, dosje të brendshme të palosura, që ju duhet vetëm t’i gjeni e t’i zbërtheni, ashtu si edhe kodet! Epo, nuk është e lehtë të jesh agjent sekret! ', 24.95, '2024-05-30T16:24:00.024Z', 'Paper', 28, 8);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123464, './Images/144.jpg', 'Gjithcka Qe Duhet Te Dini ', '2024-05-30T16:24:00.024Z', 360, 'Kjo enciklopedi e mrekullueshme është një përmbledhje me dhjetë kapituj që grupojnë dhjetë çështje si: makinat, bimët, kafshët, dinozaurët, shkenca, hapësira e të tjera. Në brendësi të këtyre kapitujve ka informacione të shumta mbi tema të shumëllojshme. Të trajtuara me një tekst të qartë, të thjeshtë dhe ndërveprues, ato shoqërohen me 2000 ilustrime të hollësishme, informuese e argëtuese, çlodhëse e angazhuese. Te “Këndi i krijimit” që gjendet në fund të shumë çështjeve të trajtuara, do të mësoni si të krijoni me duart tuaja botën e dinozaurëve, si t’i shtoni bimët me copë, si të krijoni flutura e plot eksperimente të tjera. Fjalorthi i çdo teme ju ndihmon t’i kuptoni më mirë informacionet e marra. Fëmijët do të ndiejnë kënaqësinë e arritjes në rubrikën “Tani e dini!” që gjendet në fund të çdo kapitulli dhe që i ndihmon ata të kuptojnë se kanë mësuar vërtet “gjithçka që duhet të dinë”! ', 25.00, '2024-05-30T16:24:00.024Z', 'Paper', 28, 7);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355680, './Images/350.jpeg', 'Probleme Interesante Matematikore ', '2024-05-30T16:24:00.024Z', 165, 'Në librin e pestë “Probleme interesante matematikore”, si vazhdimësi e katër librave të mëparshëm , janë gjithsej 275 probleme matematikore , si edhe zgjidhjet e tyre nga fusha e matematikës , shumica e të cilave janë interesante dhe argëtuese. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 32, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355381, './Images/385.jpg', ' Jul Qezari 15', '2024-05-30T16:24:00.024Z', 192, ' “Jul Çezari” është tragjedia e parë e Shekspirit dhe një nga tri tragjeditë me temë historike. Shekspiri shkruan për Cezarin, Brutin dhe Mark Antonin duke u mbështetur në historianin e lashtë romak Plutarkun. Jul Cezari është gjithashtu një tragjedi, por, pavarësisht nga titulli, karakteri tragjik i pjesës është Bruti, romaku fisnik, vendimi i të cilit, për të marrë pjesë për hir të lirisë, e çon atë, në një konflikt personal dhe vendin e tij në një luftë civile. ', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 11, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (12355482, './Images/386.jpg', 'Shtepi Kukulle ', '2024-05-30T16:24:00.024Z', 283, ' Një dhomë e mobiluar mirë e me shije, por pa luks. Në fund, nga ana e djathtë, një derë të shpie në korridorin e hyrjes. Një derë tjetër, po në fund, por nga ana e majtë, të çon në dhomën e punës të Helmerit. Midis këtyre dy dyrerve është një piano. Në mes të murit të anës së majtë është një derë dhe, ca më pas, një dritare. Pranë dritares është një tryezë e rrumbullakët me një karrige të madhe dhe me një divan të vogël. Në murin e anës së dhjathtë, në fund, është një derë tjetër.', 5.00, '2024-05-30T16:24:00.024Z', 'Paper', 11, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123567, './Images/268.jpg', ' Antologjia E Letersise Turke', '2024-05-30T16:24:00.024Z', 406, 'Antologji me shembuj përfaqësues nga periudhat kryesore të zhvillimit të letërsisë turke, duke filluar nga periudha paraislame e deri më sot. ', 7.50, '2024-05-30T16:24:00.024Z', 'Paper', 59, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123362, './Images/39.jpg', 'Qyteti Dhe Patologjitë E Jetës Urbane', '2024-05-30T16:24:00.024Z', 400, 'Në qendër të librit vendosen problemet e qytetit, të jetës së përbashkët qytetare, duke i parë dhe vlerësuar me optikën e sociologut. Të gjitha këto e bëjnë librin një “manual” që të ndihmon në perceptimin e zhvillimeve dinamike të qytetit dhe problematikave komplekse të jetës urbane. Është rezultat i angazhimit qytetar dhe intelektual dhe, pa dyshim, edhe i një veprimtarie e pune studimore këmbëngulëse disavjeçare. Nuk është vetëm libër me dimension teorik dhe me synime intelektuale e sociologjike, sepse lënda, përmbajtja dhe struktura e librit është ndërtuar në mënyrë të tillë, ku të reflektohen jo vetëm ide sintetike, që janë produkt i shumë analizave e refleksioneve personale të autorit, por dhe synimi për të ndikuar në trajtë etike e empirike për të gjitha problemet e zhvillimit të qytetit, përmirësimit të statusit të qytetarit dhe dinamikës së jetës urbane. Ide dhe njohuri që janë aktuale, pikërisht tani që ndodhemi në “zemër” të epokës tranzitore demokratike në vendin tonë. ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 31, 17);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123568, './Images/269.jpg', 'Fotografite E Kujtimeve ', '2024-05-30T16:24:00.024Z', 218, '  “Fotografitë e kujtimeve” – roman bashkëkohor me vlera letrare dhe islame.', 4.00, '2024-05-30T16:24:00.024Z', 'Paper', 59, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123467, './Images/149.jpg', 'Me Quajne Marko Polo ', '2024-05-30T16:24:00.024Z', 64, ' Marko Polo vinte nga një familje veneciane. Ai ndërmori një udhëtim marramendës në Kinë, kur ishte vetëm shtatëmbëdhjetë vjeç. Po aq vite qëndroi në shërbimin e Khanit të Madh dhe jetoi në kulmin e lulëzimit të Dinastisë Mongoliane. Gjatë kësaj periudhe arriti të mësonte për organizmin e atij vendi të paanë, veprat kolosale publike, sistemin e postave, përdorimin e parasë prej letre apo punimin e mëndafshit. Pas kthimit në vendlindje, që nga burgu, ku u burgos rastësisht, Marko Polo mundi të na tregonte përvojën e tij fantastike të mbledhur në Librin e mrekullirave të botës. Konsiderohet si një nga udhëtarët më të mëdhenj të mesjetës, si dhe ai që “zbuloi” Kinën para perëndimorëve. ', 3.95, '2024-05-30T16:24:00.024Z', 'Paper', 13, 6);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123468, './Images/152.jpg', 'Me Quajne Isak Njuton ', '2024-05-30T16:24:00.024Z', 65, 'Përshëndetje, më quajnë Njuton, Isak Njuton. Në moshën tuaj adhuroja të ndërtoja shpikjet e mia të çuditshme. Një ditë ngrita në fluturim disa balona, të cilave u ngjita disa drita që i frikësuan fqinjët. Sa gallatë! Më pëlqenin shumë edhe orët, kështu që arrita të ndërtoja një orë që funksiononte me forcën e ujit, dhe një ore die!lore, që më bëri të famshëm në gjithë vendin. Por nuk njihem nëgjithë botën për këtë orë, por per një kokërr mollë. Po, po, për një kokërr të thjeshtë molle! ',3.95, '2024-05-30T16:24:00.024Z', 'Paper', 13, 8);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123363, './Images/40.jpg', 'Shqiperia E Bukur', '2024-05-30T16:24:00.024Z', 322, 'Albumi i parë i studiuesit dhe publicistit Ndriçim Kulla me titull “Shqipëria e Bukur”, servir një informacion të bollshëm fotografik dhe interpretime kulturoro-historik.Ai është unik në llojin e vet, sepse për herë të parë prezanton përpara lexuesit shqiptar dhe turistit të huaj bukuritë natyrore të Shqipërisë, si dhe pasuritë e jashtëzakonshme të trashëgimisë kulturoro-historike. Nëpërmjet këtij botimi, autori bën një promovim elitar të vlerave turistike të aksesueshme për t’u vizituar dhe destinacioneve të shumta që ka vendi ynë.Albumi, i cili është botuar në shqip dhe në anglisht, shoqërohet me një hyrje nga ana e autorit, gjithashtu edhe me një guidë orientuese për të gjitha pamjet.Ky projekt ka si qëllim kryesor prezantimin dhe promovimin e bukurive të vendit tonë nga Vermoshi pjesa më veriore, e deri në Buntrint, i cili përkon edhe me ekstremin jugor të Shqipërisë. ', 43.20, '2024-05-30T16:24:00.024Z', 'Paper', 45, 13);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123390, './Images/60.png', 'Përtej ', '2024-05-30T16:24:00.024Z', 326, ' Është një libër që zhyt lexuesit në një botë të re dhe të mrekullueshme përtej kufijve të njohur. Me një histori magjepsëse dhe personazhe të thelluar, libri eksploron tema të tilla si aventura, miqësia dhe guximi. Përmes narrativës së tij tërheqëse dhe ilustruarve të bukura, "Pertej" fton lexuesit të shkojnë përtej imagjinatës dhe të zbulojnë mundësi të pafundme.', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123581, './Images/285.jpg', ' Vrasje Ne Orient Ekspres', '2024-05-30T16:24:00.024Z', 401, 'Ndërsa udhëton në trenin legjendar Orient Ekspres, detektivi i pakrahasueshëm e i paarritshëm Herkul Puaro është i detyruar të merret me një vrasje misterioze. Ndërsa treni është i bllokuar në dëborë, një ndër pasagjerët godet për vdekje zotërinë e pasur Rashet. Në të vërtetë, vrasësi është i fshehur midis udhëtarëve të trenit, por në vështrim të parë duket se asnjë prej tyre nuk ka një motiv të fortë për ta kryer këtë krim.  ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 66, 23);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123376, './Images/53.png', 'Arkitektura Sizmike', '2024-05-30T16:24:00.024Z', 205, 'Roli i Arkitektures ne reagimin sizmik te strukturave ', 48.40, '2024-05-30T16:24:00.024Z', 'Paper', 13, 22);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123377, './Images/54.jpg', 'Dimensioni I Perjetimit Dhe Perceptimit Ne Artitekture', '2024-05-30T16:24:00.024Z', 205, 'Trajtimi i arkitekturës ndryshe nga zakonisht, në një dimension më tepër shpirtëror e emocional, është thelbi i këtij libri. Hapësira arkitektonike dhe mënyra se si e perceptojmë atë, si dhe përjetimi i kësaj hapësire, janë indikatorë të fuqishëm në jetën e njeriut. Prandaj, këto dy çështje janë trajtuar jo vetëm në rrafshin arkitektonik, por edhe në atë filozofik, psikologjik e emocional. ', 10.00, '2024-05-30T16:24:00.024Z', 'Paper', 46, 12);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123361, './Images/38.jpg', 'Parqet Kombetare Te Shqiperise', '2024-05-30T16:24:00.024Z', 300, 'Hapësira gjeografike dhe trashëgimia natyrore e saj është një nga dimensionet e rëndësishme të çdo qytetërimi. Shoqëria njerëzore e trashëgon atë historikisht, nga brezi në brez dhe këto vlera duhet t’ua përcjellë të padëmtuara apo të tjetërsuara brezave të ardhshëm. Ajo është vlerë natyrore, por edhe materiale, shoqërore e shpirtërore dhe, si e tillë, është edhe vlerë kulturore. ', 40.00, '2024-05-30T16:24:00.024Z', 'Paper', 13, 4);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123569, './Images/270.jpg', 'Ne Kerkim Te Gjuhes Dhe Mendimit ', '2024-05-30T16:24:00.024Z', 287, 'Unë personalisht qysh nga mesi i studimeve të mija erdha në përfundim, që një fenomen interesant ndoshta jo vetëm linguistik është edhe anliza në mes të gjuhës dhe mendimit. Në këtë përfundim kanë ardhur edhe shumë shkencëtarë të njohur gjerman gjatë punës së tyre shkencore, andaj është ky ndoshta edhe një proces i shkencëtarëve që merren me linguistikë. domosdoshëm evolutiv i shkencëtarëve që merren me linguistikë ', 7.95, '2024-05-30T16:24:00.024Z', 'Paper', 32, 17);
