INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123450, './Images/112.jpg', 'Doktrina Shok ', '2024-05-30T16:24:00.024Z', 401, 'Libri "Doktrina Shok" është një sipërmarrje masive e kurajoze, që shpreh kritikën pasionante të Naomi Klein ndaj dhunës që shoqëron imperializmin ekonomik amerikan. Kritikë kjo, që jo vetëm konsiderohet e nevojshme, por është tepër urgjente. ', 15.00, '2024-05-30T16:24:00.024Z', 'Paper', 48, 12);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123470, './Images/150.jpg', 'Enciklopedia Ime E Pare Perse ', '2024-05-30T16:24:00.024Z', 96, 'Enciklopedia Ime E Pare Perse ', 11.95, '2024-05-30T16:24:00.024Z', 'Paper', 23, 5);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123405, './Images/83.jpg', ' Jeta Ime Autobiografi', '2024-05-30T16:24:00.024Z', 401, '"Jeta ime" e Trockit, vëllimi i të cilit po i jepet sot lexuesit shqiptar, përmban veprimtarinë e Trockit prej fillimit të vitit 1917 e deri në fund të jetës së tij. Pa dyshim, kjo është edhe pjesa më interesante dhe më përfaqësuese e jetës së tij. ', 7.00, '2024-05-30T16:24:00.024Z', 'Paper', 11, 89);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123388, './Images/67.jpg', 'Megjithate Publike ', '2024-05-30T16:24:00.024Z', 401, 'Megjithate Publike" është një libër që eksploron ide dhe opinione mbi çështje të rëndësishme shoqërore dhe artistike. Me një qasje kritike dhe analitike, autori ndan pikëpamjet e tij për zhvillimet aktuale dhe sfidat me të cilat përballet shoqëria bashkëkohore, duke ofruar një perspektivë të thelluar dhe të menduar mirë.', 14.95, '2024-05-30T16:24:00.024Z', 'Paper', 21, 18);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123389, './Images/58.png', 'Ngjyrose Lumbardhin ', '2024-05-30T16:24:00.024Z', 280, ' Artistja Rina Krasniqi është ftuar të krijojë ilustrime historike të kinemasë Lumbardhi, bazuar në hulumtimin e kryer nga studiuesit Tevfik Rada dhe Bengi Muzbeg në kuadër të projektit Kinofigurimi ', 12.80, '2024-05-30T16:24:00.024Z', 'Paper', 46, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123473, './Images/153.jpg', 'Libri Im I Pare Me Pyetje Dhe Pergjigje Per Boten ', '2024-05-30T16:24:00.024Z', 135, 'A e dini që bota jonë është plot me gjëra të çuditshme e të pazakonta? Doemos që keni dëgjuar e keni parë. Po kërshëria dhe dija nuk kanë fund. Ky është nga ata lloj librash që ju zgjon kërshërinë e kështu e ju bën të mësoni më shumë. Po si? Në çdo faqe ka disa pyetje. Secila prej tyre është shkruar në kanatin e një dritareje, e cila është zbukuruar me mjeshtëri me ilustrimet përkatëse. Po të hapni kanatin, do të gjeni përgjigjen. Sa i madh ishte Titaniku? Kur u shpik akullorja? Çfarë është treni fluturues? Pse ka këtë emër Deti i Vdekur? Ku u shpikën fishekzjarrët? Cili vend e feston i pari Vitin e Ri? Çfarë ndodh kur kapërdin çamçakëzin?. Hapni dritaret e lexojini përgjigjet. ', 7.50, '2024-05-30T16:24:00.024Z', 'Paper', 60, 10);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123555, './Images/260.jpg', 'Dialog Me Poezine E Lasgush Poradecit ', '2024-05-30T16:24:00.024Z', 229, 'Poezia lasgushiane ështe poezi, e cila arrin te ruajë identiitetin e vet, madje në një kohë të mbushur me ngjarje të shumta e të ërreta, të cillat, jo rrallë, me apo pa vetëdije,ndikonin në rrugën zhvillimore të artit. ', 4.00, '2024-05-30T16:24:00.024Z', 'Paper', 61, 8);
INSERT INTO Books (ISBN, image, Title, PublicationDate, PageNumber, Description, Price, DateOfadition, Type, PublishingHouseId, StockId) VALUES (123466, './Images/146.jpg', ' Enciklopedia E Perseve', '2024-05-30T16:24:00.024Z', 165, ' A e dini pse? Një enciklopedi e rrallë, e ndërtuar në mënyrën më të përshtatshme për fëmijët: duke iu përgjigjur pyetjeve të tyre të shumta. Nëpërmjet fotove të përshkruara hollësisht, si dhe kurioziteteve nga fusha të ndryshme të dijes, ajo ngre pyetje dhe jep përgjigje, duke e bërë mënyrën e të mësuarit më të thjeshtë dhe ndërvepruese. Të gjitha pyetjet që fëmijët ngrenë mbi shkencën, gjeografinë, historinë, njerëzit dhe natyrën, do të marrin përgjigje në këtë libër të këndshëm dhe angazhues. Fotografitë trepërmasore do t’i ndihmojnë ata t’i kuptojnë plotësisht përgjigjet që u jepen pyetjeve të tyre. Ata mund të shohin nga brenda një vullkan, një lule apo edhe trupin e gjarprit, gjë që e bën vërtet të prekshëm informacionin interesant që marrin. A e dini pse kjo enciklopedi është ajo që duhet? Sepse di të japë përgjigje!', 11.95, '2024-05-30T16:24:00.024Z', 'Paper', 28, 13);
